style.css:
@-moz-document domain("bigsoccer.com") {

/* Decrease the padding between messages */
.messageList {
padding: 0px;
}

.messageList .message {
padding-top: 5px;
padding-bottom: 5px;
margin: 5px 5px;
border-top: 1px solid #CCC; /* thin out the message divider */
}

/* Slight decrease in space between poster info and message */
.message .messageInfo {
margin-left: 150px;
margin-right: 0px;
}


/* Remove the muted grey look on message authors in a post */
a.username.author {
 color: #000000;
}

/* Make the break between message content and meta data visible */
div.messageMeta {

border-top: 1px solid #ccc;
border-right-style: none;
}

/* Decrease the spacing between forums in the watched forum list */
.nodeList .node.level_1 {
margin-bottom: 5px;
}

/* Hide avatars in thread list, but still show the "i've replied to this thread indicator */
.discussionList .posterAvatar {
 width: 28px;
}
span.avatarContainer a.avatar {
display: none !important;
}

span.avatarContainer a.avatar.miniMe {
display: table-cell !important;
left: 2px;
}

/* Remove muted grey color on thread list author */
div.posterDate.muted a.username {
color: #000000;
}

/* Remove the mouse over on the page navigation items in the thread list */
.discussionListItem .itemPageNav {
visibility: visible !important;
}

.adverts {
display: none !important;
}

}
