style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wer-kennt-wen.de") {
#navigation{
background: -moz-linear-gradient(top,  #7cc2ff 0%, #3f87c6 50%, #006fc4 51%, #59b1ff 100%)!important;
text-shadow:black 0px 0px 3px,
            black 0px 0px 3px !important;
}
li.active a{
color:rgba(255,255,255,1)!important;
}
li:hover a{
color:white!important;
}
h2>span{
background: -moz-linear-gradient(top,  #7cc2ff 0%, #3f87c6 50%, #006fc4 51%, #59b1ff 100%)!important;
border-top-right-radius: 10px!important;
border-top-left-radius: 10px!important;
}
h2{
background:none!important;
}
.jquery-ui-tab, .jquery-ui-tab a{
background: -moz-linear-gradient(top,  #7cc2ff 0%, #3f87c6 50%, #006fc4 51%, #59b1ff 100%)!important;
text-shadow:black 0px 0px 3px,
            black 0px 0px 3px !important;border-top-right-radius: 10px!important;
border-top-right-radius: 10px!important;
border-top-left-radius: 10px!important;
}
.jquery-ui-tab.active a{
color:rgba(255,255,255,1)!important;
text-shadow:black 0px 0px 3px,
            black 0px 0px 3px !important;
}
.jquery-ui-tab a{
color:rgba(255,255,255,.5)!important;
text-shadow:black 0px 0px 3px,
            black 0px 0px 3px !important;
}
.jquery-ui-tab-bar{
border-color:#59B1FF!important;
}
#jn_textContainer, #myGBPostContainer, #myBlogContainer{
background: -moz-linear-gradient(top,  #7cc2ff 0%, #3f87c6 50%, #006fc4 51%, #59b1ff 100%)!important;
position:relative!important;
top:-12px!important;
border:none!important;
}
#jn_text, .attUrl, #create_jn, .UI-placeHolder, #searchquery, #invite_email, #sendToWrap, #subject, #body, #search_text, #invite_firstName, #myGBPost, #myBlogPostSubject, #myBlog{
background: -moz-linear-gradient(top,  #f6f8f9 0%, #e5ebee 50%, #d7dee3 51%, #f5f7f9 100%)!important;
}
.attachLink a, .count_jn_text, .gbStyleLink{
color:white!important;
}
.uiButton{
background: -moz-linear-gradient(top,  #f6f8f9 0%, #e5ebee 50%, #d7dee3 51%, #f5f7f9 100%)!important;
min-width:86px!important;
text-align:center!important;
}
#xc358s8t, .hoverCommon{
background: -moz-linear-gradient(top,  #ebf1f6 0%, #abd3ee 50%, #89c3eb 51%, #d5ebfb 100%)!important;
}
.hoverCardWrapper{
background: -moz-linear-gradient(top,  #f6f8f9 0%, #e5ebee 50%, #d7dee3 51%, #f5f7f9 100%)!important;
}
.wkwHoverCard{
box-shadow:black 0px 0px 5px,
           black 0px 0px 5px !important;
}
.persondata{
background: -moz-linear-gradient(top,  #f6f8f9 0%, #e5ebee 50%, #d7dee3 51%, #f5f7f9 100%)!important;
}
.wkwNotifyBubble{
background: -moz-linear-gradient(top,  #ff0000 0%, #c90000 50%, #a90000 51%, #f60000 100%)!important;
text-shadow:none!important;
border:none!important;
padding-top:2px!important;
padding-bottom:2px!important;
padding-left:4px!important;
padding-right:4px!important;
box-shadow:#A00000 0px 0px 5px,
           #A00000 0px 0px 5px !important;
}
}
