style.css:
@-moz-document url-prefix("http://cnbeta.com/"), url-prefix("http://www.cnbeta.com/") {
body { background: #FFF; }
#sideBar, #AdForward, #confident, #h_right, #logo center, #foot, #head, .title, .subTitle, .detail, .author { display: none; }
.topic a { font-size: 24px; line-height: 24px; color: #202020; padding: 0; margin: 0; font-family: "Hiragino Sans GB W3","HelveticaNeue57Condensed" !important; }
#ADcon0 .newslist { background: none; border: none; border-bottom: solid 3px #f1f1f1; }
#main #content { width: auto !important; }
#news-bottom-list { background: #CDCDCD; }
#news-bottom-list a { color: #f1f1f1; line-height: 20px; }
#active .newslist .desc { font-size: 14px !important; color:#555; }
#active .newslist .desc b { display: none !important; }
}

@-moz-document url-prefix("http://cnbeta.com/articles/"), url-prefix("http://www.cnbeta.com/articles/") {
body { background: #fff; }
h3#news_title { font-size:24px; color:#202020; font-family: "Hiragino Sans GB W3","HelveticaNeue57Condensed" !important; } 
div#head,div#menu,p#news_author,div#feedback,div#news_foot,div#mark,div#post,div.digbox,p#news_foot,div.subTitle,dd.re_mark{ display:none;}
#content #contentWrapper,#newsBox #news,#g_content dl, #n_content dl { border:none;}
#main, #content, #news, #news_title,#news_content { display: block !important;}
#newsBox { width:950px;}
#newsBox #news,#normal { width:800px; margin:0 auto;}
#contentWrapper,#news #news_content{ width:100%;}
#news #news_content p img { display:block;margin:0 auto;}
#g_content dl dt.re_author span, #n_content dl dt.re_author span { color:#fff; text-shadow:0 0 0 #fff; border:none;}
#g_content dl dt.re_author span strong , #n_content dl dt.re_author span strong { color:#008900;}
.feedBack { min-height:0;}
.feedBack .fb_title { background:#fefefe; padding: 0px 5px; text-indent:0;}
#n_content dl dd.re_detail { text-indent:2em;}
}
