style.css:
@-moz-document domain("extensions.gnome.org") {
* { font-family: Open Sans, Droid Sans, sans !important; }

.extension.configurable .configure-button, .extension.upgradable .upgrade-button { margin-top: -4px }

.beta-banner { display: none; }

#gnome_header { padding: 0; }

.fsui-dropdown-item, .paginator .number, .fsui-dropdown-link { cursor: default; }

.fsui-dropdown { margin-top: 0px; }
.fsui-dropdown-link { border-radius: 4px 4px 0px 0px; }

/* not my fault i can't read .1pt font */
.paginator { font-size: 11pt; }

