@-moz-document domain(9gag.com) {
/* Removes Featurebar */
.feature-bar {
   display: none !important;
}
/* Removes Twitter */
#head-bar h1 .header-twitter-follow {
    display: none;
}


/* Removes Facebook - Add to timeline */
#special-button {
    display: none;
}
/* Removes Social Block */
.side-dock .social-block {
    display: none;
}

#view-info {
    display: none;
}
/* Hide iPhone App Ad */
.download-block {
    display: none;
}

}
