style.css:
@-moz-document domain("facebook.com") {
/* 1.0.11 */

#BuddylistPagelet, #fbDockChatBuddylistNub{
	position: fixed !important;
	left: 0px !important;
	margin-left: 0px !important;
}

/* ---------------- Facebook SideBar Support ---------------- */
.fbChatSidebar{
	left: 0px !important;
	z-index: 0 !important;
}

.sidebarMode #globalContainer{
	padding-right: 0px !important;
	padding-left: 85px !important;
}

/*.fbDock,*/ .fbChatSidebar{
	position: fixed !important;
	left: 0px !important;
}

#ChatTabsPagelet{
	position: fixed !important;
	right: unset !important;
	left: 240px !important;
}

.fbChatSidebar .fbChatOrderedList{
	padding-top: 50px !important;
}

/* ------------- Facebook Chat (Social Reviver) ------------- */
/*.rNubContainer .fbNubFlyout{
    position: fixed !important;
	right: unset !important;
	left: 0px !important;
}*/

.fbNub, .fbNubGroup, .fbDock .nubContainer > div, .fbDock .fbNubGroup > div{
	float: right !important;
}

/* ----------------- Facebook Notifications ----------------- */

#pagelet_dock, .UIBeeper_Full, .enableBeeper#fbNotificationsFlyout .beeperNub{
	position: fixed !important;
	right: 100px !important;
	bottom: 50px !important;
	width: 200px !important;
	z-index: 999999999 !important;
}

.UIBeeper_Full{
	background-color:#683433 !important;box-shadow:0 0 8px rgba(139,86,85,0.50);
}

.fbDockWrapper .fbDock{
	border-right: 0px solid transparent !important;
}

._2a9, .-cx-PRIVATE-notificationBeeper__list, #pagelet_dock [data-gt*=beeper]{
	position: fixed !important;
	left: 75% !important;
	bottom: 2% !important;
}

.uiSelectorRight .uiSelectorMenuWrapper{
	right: auto !important;
}
}
