style.css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul"); /* set default namespace to XUL */

#urlbar,
.searchbar-textbox {
	font-size: 12.5px !important}
#identity-box {
	background-color: Transparent !important;
	font-size: 12px !important}
#notification-popup-box {
	background: -moz-linear-gradient(left, rgba(255,255,255,0), White 97%) padding-box !important;
	padding-right: 0 !important;
	padding-left: 6px !important}

#PopupAutoCompleteRichResult {
	-moz-appearance: none !important;
	background-color: White !important;
	border-radius: 0 0 2.5px 2.5px !important;
	border: 1px solid #5EB8FD !important;
	border-top: 0 !important;
	padding: 0 !important;
	margin-top: -2px !Important;
	box-shadow: none !important}

#main-window[privatebrowsingmode=temporary] #urlbar,
#main-window[privatebrowsingmode=temporary] #PopupAutoCompleteRichResult {
	background-image: url("chrome://mozapps/skin/extensions/stripes-error.png") !important}
#main-window[privatebrowsingmode=temporary] #PopupAutoCompleteRichResult {
	background-position: -1px -21px !important;
	background-repeat: repeat-x !important}

#main-window[tabsontop=false] #PopupAutoCompleteRichResult,
#PopupAutoCompleteRichResult:-moz-lwtheme {
	background: White padding-box !important;
	border: 1px solid rgba(0,0,0,.32) !important;
	border-top: 0 !important}
#PopupAutoCompleteRichResult .autocomplete-richlistbox {
	background: 0 !important}
#PopupAutoCompleteRichResult .autocomplete-richlistitem {
	padding-left: 3px !important;
	margin-right: -3px !important;
	margin-left: -3px !important}
#PopupAutoCompleteRichResult .ac-site-icon {
	margin: 0 7px !important}
#PopupAutoCompleteRichResult .ac-title-box {
	color: Black !important}
#PopupAutoCompleteRichResult .ac-url-box {
	color: -moz-nativehyperlinktext !important}
#PopupAutoCompleteRichResult .ac-url {
	vertical-align: top !important}
#PopupAutoCompleteRichResult .ac-title {
	margin: 0 !important}
