style.css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

treechildren::-moz-tree-row(even) {
  background-image: -moz-linear-gradient(top, rgba(255, 255, 255, .08),
                                              rgba(255, 255, 255, .08));
  background-image: linear-gradient(to bottom, rgba(255, 255, 255, .08),
                                               rgba(255, 255, 255, .08));
}

treechildren::-moz-tree-row(odd) {
  background-image: -moz-linear-gradient(top, rgba(0, 0, 0, .08),
                                              rgba(0, 0, 0, .08));
  background-image: linear-gradient(to bottom, rgba(0, 0, 0, .08),
                                               rgba(0, 0, 0, .08));
}

treechildren::-moz-tree-row(selected) {
  background-color: -moz-menuhover !important;
}

treechildren::-moz-tree-cell-text(selected),
treechildren::-moz-tree-cell-text(selected, open) {
  color: -moz-menuhovertext !important;
}
