style.css:
@-moz-document domain("mlpchan.net") {

.boardlist.top {
  position: fixed;
  top: 0;
  left: 13px;
  width: 100%;
}

/* 
 * when .boardlist.top is pinned, it's no longer on the page, so
 * the header needs more margin to compensate for the missing block:
 * 16px lost from the padding and 16px from the font-size
 * as well as the header normally has a 1em (~16px) default margin-top
 */
header { margin-top: 48px !important; }

}
