style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/** Firefox 14+ Round back button as FF13 on hover **/
/** This style was inspired by and is based on code from http://userstyles.org/styles/67212/firefox-14-navbar-and-tabs-style-from-ff9-f13 **/

@-moz-document url-prefix("chrome://browser/content/browser.xul") {

/** Forward Button **/
#nav-bar #forward-button {
    mask: url(chrome://browser/content/browser.xul#winstripe-keyhole-forward-mask);
    -moz-margin-start: -8px !important;
    padding-left: 5px !important;
}

/** Forward Button image **/
#nav-bar #forward-button .toolbarbutton-icon {
    padding-right: 5px !important;
}

/** Back Button **/
#nav-bar #back-button {
    -moz-padding-start: 5px !important;
    margin-right:-5px !important;
    position:relative !important;
    z-index:1 !important;
}

/** Back Button image **/
#nav-bar #back-button .toolbarbutton-icon {
    border-radius: 15px !important;
    padding: 5px !important;
    margin: -4px 0 !important;
    -moz-image-region: rect(18px, 20px, 38px, 0) !important;
}

}
