style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.majorleaguegaming.com/live") {
  /*@MLG:  live video player.  quickpatch for hidden video player controls*/
  #stage > .cell {max-height: 93% !important;}  /* #stage > .cell {height: 590px !important;} */
}

@-moz-document url-prefix("http://tv.majorleaguegaming.com/videos/") {
    /* bigger vods (archived videos) */
    div.video, object.player {
        width: 1000px !important;
        height: 640px !important;
    }
    div.right-column {
        display: block !important;
        float: none !important;
    }
}
