style.css:
/* 
 * ======================================================================================
 * Livejournal Modern 
 *
 * Author    : BananaMuffin
 * Version   : 2.0.0 Chrome
 * Customize : Visit my journal for editing instructions - 
 *             http://eevereon.livejournal.com/
 * Notes     : This style requires manual update (Stylish won't alert you of updates either) - 
 *             http://userstyles.org/styles/70496/
 * ======================================================================================
*/ 

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("livejournal.com") {
/* Font Import */
@font-face {
    font-family: "BebasNeue";
    src: url("http://bit.ly/sw_BebasNeue-eot");
    src: url("http://bit.ly/sw_BebasNeue-eot-iefix") format("embedded-opentype"),
        url("http://bit.ly/sw_BebasNeue-woff") format("woff"),
        url("http://bit.ly/sw_BebasNeue-ttf") format("truetype"),
        url("http://bit.ly/sw_BebasNeue-svg") format("svg");
    font-weight: normal;
    font-style: normal;
}

/* ============================== CUSTOMIZATION SECTION ================================ */

/*[[theme]]*/

/* ============================ end CUSTOMIZATION SECTION ============================ */


/* ==================================== STYLE =================================== */

/*[[layout]]*/


/* ==== SITE GLOBAL ==== */

/* ~~~ Remove ads that mess with the layout ~~~*/
.ljadwrapper-app-vertical, .ljad, .ljadskyscraper, .ljadmedrect, .ljad5linkunit, .ljadbadge, .ljadentrybox, .ljadafter-post-a, .ljadwrapper-app-full-width
{ height: 0!important; width: 0!important; margin: 0!Important; visibility: hidden!important; }

/* ~~~ Side Shadows ~~~ */
.logged-in #content-inner:before, .logged-in #content-inner:after,
.logged-out #content-inner:before, .logged-out #content-inner:after,
#footer-links:before, #footer-links:after {
content: "";
background: url("http://bit.ly/sw_Shadows-PNG") repeat-y;
display: block;
position: absolute;
width: 50px;
}
.logged-in #content-inner:before, #footer-links:before,
.logged-out #content-inner:before, #footer-links:before { /* left side */
background-position: 0 0;
left: -50px;
}
.logged-in #content-inner:after, #footer-links:after,
.logged-out #content-inner:after, #footer-links:after { /* right side */
background-position: -50px 0;
right: -50px;
}
.logged-in #content-inner:before, .logged-in #content-inner:after,
.logged-out #content-inner:before, .logged-out #content-inner:after { /* content shadows */
min-height: 100%;
top: -63px;
bottom: 0;
}
#footer-links:before, #footer-links:after { /* footer shadows */
height: 200px;
top: 0;
}

/* ~~~ Buttons ~~~ */
#content textarea, #content input[type="text"]:not(#draftstatus), #content input[type="password"],
#content select, .b-userpicselector-inner input, .b-massaction input, .b-ljbutton button, 
input#basic_query, .mailfinder input { /* text input */
-webkit-appearance: none!important;
padding: 3px;
}
#content input[type="submit"], #content input[type="reset"], #content input[type="button"],
.theme-item .theme-button, span.Pages input, button.canyon-side, .basic button, 
form button[type="submit"] { /* click button */
-webkit-appearance: none!important;
padding: 3px 10px!important;
border-radius: 8px!important;
}
.standout .container { border: 0!important; }
#settings_save { border-radius: 8px; }

/* ~~~ Contextual Popup ~~~ */
.b-popup.bubble-node.b-popup-contextual.b-popup-noclosecontrol .b-popup-outer {  /* transparent border */
box-shadow: 1px 2px 8px rgba(0,0,0,0.5);
}
.b-popup.bubble-node.b-popup-contextual.b-popup-noclosecontrol .b-popup-outer > .b-popup-inner { /* inner */
margin: 2px!important;
padding: 8px!important;
}

.logged-in #content, .logged-out #content {
min-height: 810px;
margin: 0 auto!important;
padding: 10px 12px 20px 12px!important;
top: 0!important;
z-index: 1; /* move below banner but above #NavigationWelcomeLogo */
}

/* ~~~ Breadcrumbs ~~~ */
#ljbreadcrumbs {
cursor: default;
width: 100%;
position: relative;
padding: 0 10px 0 0;
left: -10px;
border-width: 0 0 1px 0!important;
color: transparent;
}
#ljbreadcrumbs:first-child { padding-left: 10px; }
#ljbreadcrumbs a, #ljbreadcrumbs b {
display: inline-block;
position: relative;
padding: 0 0 8px 0;
}
#ljbreadcrumbs a:after {
content: "";
display: inline-block;
margin: 0 0 0 10px;
}

/* ~~~ Top menu ~~~ */
div#navigation { /*  navigation div */
position: relative!important;
margin: 0 auto!important;
height: 174px!important;
z-index: inherit!important; /* removes default 200 index */
}
div#navigation:before { /* menu center overlay shadow */
content: "";
display: block;
position: absolute;
width: 100%;
height: 104px;  /* 104px */
top: 70px;  /* 70px */
margin: 0;
box-shadow: 0px 7px 8px -8px rgba(0,0,0,0.8);
z-index: 2; /* move above #content */
}
#NavigationWelcomeLogo { /* menu back - must be taller than #WelcomeLinks */
background: url("http://bit.ly/sw_MeshIcons-PNG") repeat-x!important;
background-position: 0 -290px!important;
overflow-x: hidden;
height: 180px!important; /* 180px */
top: 60px!important; /* 60px */
}
#NavigationWelcomeLogo:before { /* menu ribbon */
content: "";
display: block;
position: absolute;
width: 100%;
height: 55px;
top: 51px; /* 51px */
}
#NavigationWelcomeLogo:after { /* menu ribbons stitching */
content: "";
display: block;
position: absolute;
width: 100%;
height: 49px;
top: 57px; /* 57px */
}

/* ~~~ Top userpic ~~~ */
#Userpic { /* just a container */
position: relative!important;
height: 0!important;
margin: 0 auto!important;
top: 75px!important;
border-radius: 80px;
}
#Userpic > a { /* resize userpic link */
display: block;
width: 77px!important;
height: 77px!important;
}
#defaultpic, #NoUserpic { /* actual userpic */
background-size: 80px 80px;
width: 77px!important;
height: 77px!important;
margin-left: -10px!important;
border: 4px solid white;
border-radius: 80px;
box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.75);
}
#defaultpic, #NoUserpic img { /* actual userpic */
background-size: 80px 80px;
width: 77px!important;
height: 77px!important;
margin-left: -10px!important;
border: 4px solid white;
border-radius: 80px;
box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.75);
}
#defaultpic:hover { opacity: 0.7; }

/* ~~~ Dropdown ~~~ */
.b-navbar { /* dropdown menu container */
position: relative!important;
min-width: 995px!important;
margin: -65px auto!important; /* -65px auto */
z-index: 2!important;
}
.b-navmenu { /* dropdowns left spacing for userpic */
margin: 16px auto!important; /* 16px auto */
padding: 0 0 0 80px!important; 
} 
.b-navmenu-submenu { 
top: 43px!important; /* move up/down */
box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.5); 
}
.b-navmenu-submenu li a { padding: 7px!important; }

.b-navbar-search { /* menu search bar */
width: 226px!important;
height: 22px;
border-radius: 8px;
margin: 0!important;
padding: 0!important;
}
.b-navbar-search input, .b-navbar-search select {
-webkit-appearance: none!important;
margin: 0!important;
}
.b-navbar-search #SearchText { /* search bar text input */
border-width: 0 1px 0 0!important;
height: 11px;
padding: 0 5px 0 0!important;
}
.b-navbar-search select { /* search bar dropdown */
border: 0!important;
width: 70px!important;
}
.b-navbar-search input[type="submit"] { /* search bar submit button */
border: 0!important;
border-top-right-radius: 7px;
border-bottom-right-radius: 7px;
padding: 5px 5px 3px 5px!important; /* chrome */
}

/* ~~~ Top menu links ~~~ */
#welcome { /* container */
position: relative!important;
margin: 0 auto!important;
padding: 0!important;
left: 0!important;
z-index: inherit!important;
}

/* ~ post | messages | friends page etc... container */
#WelcomeViewLinks { 
position: relative;
display: inline-block!important;
padding: 0 0 0 15px!important;
color: transparent!important;
cursor: default;
top: -7px;
height: 21px!important;
width: auto!important;
}
#WelcomeViewLinks ul.view { /* post | messages | friends page etc... */
display: block!important; 
position: relative;
margin: 0!important;
padding: 0!important;
text-align: right; 
opacity: 0.8;
z-index: 10!important;
}
#WelcomeViewLinks > ul.view li { /* style the li */
background: black;
display: inline-block!important; 
position: relative;
height: 21px!important;
margin: 0 6px 0 6px;
padding: 4px 9px 0 14px;
border: 1px solid #2b2b2b;
border-radius: 8px;
box-shadow: 0px 0px 4px rgba(0,0,0,0.7), inset 0px 0px 1px rgba(0,0,0,1); 
text-align: center; 
opacity: 0.8;
}
#WelcomeViewLinks > ul.view li:hover { opacity: 1; }
#WelcomeViewLinks > ul.view li a {
font-weight: normal!important;
color: rgb(209,209,209)!important; /* N */
}
#WelcomeViewLinks > ul.view li a:hover { text-decoration: none!important; }

/* ~ account | help | log out */
#WelcomeLinks { 
z-index: 10!important;
position: absolute;
margin: 10px 0;
overflow: hidden;
width: 110px;
height: 35px;
text-align: center;
color: transparent!important;
right: 0;
}
#WelcomeLinks a { display: none!important; } /* hides links when "user" button not hovered */
#WelcomeLinks:hover {  /* reveal links container when hovered */
height: 150px!important;
width: 110px;
right: 0;
cursor: default;
}
#WelcomeLinks:hover a:not([href*="account"]) { /* reveal links */
background: transparent!important;
display: block!important;
position: relative;
width: 83px!important;
margin: 0 0 -10px 10px; /* bottom margin controls spacing between links */
padding: 5px 0 5px 8px;
text-align: left!important;
z-index: 25;
}
#WelcomeLinks:hover a[href*="account"] { display: none!important; } /* removes upgrade account link for stability reasons */
#WelcomeLinks:hover a:first-child { margin-top: 40px; } /* first link */
#WelcomeLinks:hover a:last-child { /* last link */
border-bottom-left-radius: 8px; 
border-bottom-right-radius: 8px;
}
#WelcomeLinks:hover a:hover { /* links styling */
text-decoration: none!important;
background: rgba(209,209,209,0.1)!important;
}
#WelcomeLinks:before { /* "user" button */
content: "User \25BC";
background: black;
display: block;
position: absolute;
width: 85px;
padding: 7px 0 7px 6px;
top: 4px;
right: 8px;
border: 1px solid #2b2b2b;
border-radius: 8px;
box-shadow: 0px 0px 4px rgba(0,0,0,0.7), inset 0px 0px 1px rgba(0,0,0,1);
opacity: 0.8;
}
#WelcomeLinks:hover:before { opacity: 1.0; content: "User \25B2"; border-bottom-left-radius: 0; border-bottom-right-radius: 0;  }
#WelcomeLinks:hover:after { /* links box */
content: "";
background: rgba(0,0,0,0.8);
display: block;
position: relative;
width: 91px;
height: 50%;
margin: -75px 0; /* links container box move top/down */
right: -9px;
border-top-left-radius: 0; 
border-top-right-radius: 0; 
border-bottom-left-radius: 8px; 
border-bottom-right-radius: 8px; 
border-width: 0 1px 1px 1px;
border-color: #2b2b2b;
border-style: solid;
box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.5);
z-index: 10;
}

/* ~ user information container */
#welcome h2 { 
display: inline-block!important;
position: relative!important;
margin: 7px 0 0 90px!important;
z-index: 10;
}
#welcome .ljuser a[href$="profile"] { /* menu userhead replacement */
display: inline-block;
position: relative;
width: 33px!important;
height: 33px!important;
margin: 0 5px 0 0!important;
top: 6px;
}
#welcome .ljuser .i-ljuser-userhead {
width: 0!important;
height: 0!important;
padding: 0 33px 33px 0!important;
}

/* ~~~~ Content ~~~~ */
.logged-in #content-inner, .logged-out #content-inner { margin: 0 auto; }

/* ~~~~ Footer ~~~~ */
.logged-in #footer, .logged-out #footer {
background-position: 0 -98px, 0 0!important;
position: relative;
height: 200px!important;
min-width: 995px!important;
margin: 0 auto!important;
padding: 0!important;
box-shadow: 0px -5px 8px rgba(0, 0, 0, 0.3);
z-index: 6!important;
}
.logged-in #footer:before, .logged-out #footer:before {
content: "";
display: block;
position: absolute;
width: 100%;
height: 200px;
margin: 3px 0;
padding: 0;
left: 0;
}
#footer-links {
position: relative;
min-width: 971px;
margin: 0 auto;
padding: 0 12px 8px 12px!important;
border-bottom: 0!important;
border-top: 0!important;
}
#footer #footer-copyright {
padding: 2px 0 2px 0!important;
color: rgb(209,209,209)!important; /* N */
}

/* ==== FRONT PAGE ==== */
.w-head h2 { padding: 0!important; margin: 0!important; }
.w-head-in .ljuser b { font-weight: normal!important; }
.homepage-loggedin .l-sidebar .w-body { border-width: 2px 0 0 0!important; }
.w-head-corner { display: none!important; }
.appwidget h2 span, .statistics h2 span { background-position: -271px -18px!important; padding-left: 0!important; }
.appwidget-recentcomments .w-body li { border-width: 0 0 1px 0!important; }

/* ==== LJ SETTINGS ==== */
#settings_nav li a.active, #settings_nav li a:hover,
#settings_nav li.disabled, #settings_nav li a {
border-width: 1px 1px 0 1px!important;
}
#settings_nav li a {
padding: 7px 8px 7px 8px!important;
border-top-left-radius: 8px;
border-top-right-radius: 8px;
}
#settings_nav li.disabled {
padding: 8px 8px!important;
}
#settings_nav_title p {
padding: 6px 10px!important;
border-width: 0 0 1px 0!important;
}
.account td, .display td, .mobile td, .privacy td, .history td, 
.advertising td, .integration td, .extensions td, .lj_adsense td
{ border-width: 0 0 1px 0!important;
}

/* ==== MANAGE USERPICS ==== */
#uploadBox { border-radius: 8px; }

/* ==== SHOP ==== */
.w-wallet, .b-promo li, .b-promo-domainru { border-radius: 8px; }
.b-gifts .b-gifts-title { padding: 0!important; }
.b-shop .m-shop-tabs li {
border-bottom: 0!important;
border-top-left-radius: 8px;
border-top-right-radius: 8px;
}
ul.b-buy-goods { border-width: 0 1px 1px 1px!important; }
ul.m-buy-types { border-width: 0 0 1px 0!important;}
table.edged { border-collapse: collapse; } /* prices table */

.b-buy-reccurent .i-option-current { /* selection */
box-shadow: 2px 2px 2px rgba(0,0,0,0.2)!important;
}

.m-shop-tabs, .b-shop .b-buy-reccurent .b-buy-options, .b-accounts-features-items .b-accounts-features-item,
.b-accounts-features-items td, .b-payment-history-table td { border-width: 0 0 1px 0!important;}
.b-gifts-catalog ul li .item:hover, .b-gifts-catalog ul li .item_hover { box-shadow: 0 0 10px rgba(0,0,0,0.2)!important; }

/* ==== BROWSE ==== */
.appwidget-featuredposts .b-posts dl { border-width: 0 0 1px 0!important; }

/* ==== USER PROFILE VIEW ==== */
#profile_page #profile_top .actions li { border-width: 0 0 1px 0!important; }
#profile_page h3.journal_subtitle {font-weight: bold!important; }
#profile_page .section_link { float: right; }
#profile_page .section_link a {
display: inline-block;
padding: 0 10px 0 10px;
}
#profile_page .username b { font-weight: normal!important; }

/* ==== POST AN ENTRY & EDIT ==== */
#content-wrapper > table[width="100%"] tr[valign="top"] td {
width: 100%!important;
padding: 0!important;
margin: 0!important;
}
  @-webkit-document url-prefix("http://www.livejournal.com/editjournal.bml") { /* stretch edit entry page only */
    #content-wrapper > table tr td {
    width: 100%!important;
    padding: 0!important;
    margin: 0!important;
    }
  }
#updateForm { /* text editor */
min-width: 965px!important;
max-width: 100%!important;
}
#entry-form-wrapper #infobox { /* info box above editor */
min-width: 120px!important;
border-width: 0 0 0 1px!important;
}
#entry-form-wrapper #infobox ul li { /* more ways to post link */
margin: 0!important;
padding: 2px 0 2px 5px!important;
}
#metainfo-wrap li label { /* info label */
width: 50px!important;
text-align: left!important;
}
#entry-form-wrapper #userpic { /* userpic */
padding: 5px 7px 2px 5px!important;
border-radius: 8px;
}
#entry-form-wrapper #userpic img { border-radius: 8px; }
#lj_userpicselect_img { border: 1px solid transparent!important; }
#lj_userpicselect_img:hover { border-radius: 9px; }

#entry.pkg, #htmltools ul { border: 0!important; }
#htmltools.pkg { border-width: 1px 1px 0 1px!important; }
#entry ul li a {
border-top-right-radius: 8px;
border-top-left-radius: 8px;
} 
#spellcheck-results { position: relative; top: -1px; } /* remove evil blue border */
#htmltools.pkg, #draft-container.pkg { position: relative; top: -1px; } /* editor container */
#entry-form-wrapper textarea#draft { margin-top: 5px!important; } /* editor */

.cke_skin_v2 .cke_button a, .cke_skin_v2 .cke_button a:hover, .cke_skin_v2 .cke_button a:focus, 
.cke_skin_v2 .cke_button a:active, .cke_skin_v2 .cke_button a.cke_off { /* rich text menu */
padding-top: 4px!important;
border: 0!important;
border-radius: 8px;
}
.cke_skin_v2 .cke_top { border-width: 0 0 1px 0!important; } /* rich text container */

/* ==== EDIT JOURNAL ENTRIES ==== */
.b-editentry { border-width: 0 0 1px 0!important; }
.b-editentries { border-width: 1px 0 0 0!important; }

/* ==== EDIT TAGS ==== */
.proptbl .t, .proptbl .r, .proptbl .rv { border: 0!important; }

/* ==== CUSTOMIZE JOURNAL STYLE ==== */
.theme-current.pkg { /* ~ current theme container */
border-radius: 8px;
}
  .theme-current.pkg h2.widget-header span b { /* current theme header remove username font weight */
  font-weight: normal!important;
  }
  .theme-current.pkg .theme-current-links { /* current theme links box */
  border-width: 0 1px 1px 0!important;
  }
  .layout-item, .theme-item-in, .customize-content fieldset legend { border: 0!important; } /* page setup */
  .customize-content fieldset { border-width: 1px 0 0 0!important; }
  div.customize-nav, div.theme-selector-nav { /* ~ customize theme nav container */
border-top-left-radius: 8px;
border-bottom-left-radius: 8px;
box-shadow: inset -2px 0px 6px rgba(0,0,0,0.1);
}
  .customize-nav ul, .theme-selector-nav ul { /* customize theme nav menu */
  padding: 5px 0 5px 5px!important;
  width: 130px!important;
  }
  .customize-nav li.on a, .theme-selector-nav li.on a { /* selected li */
  margin: 0 0 0 -5px;
  padding: 5px 0 5px 10px!important;
  border-width: 1px 0 1px 0!important;
  }
  .theme-selector-nav li.on a {
  box-shadow: 0px 2px 5px -3px rgba(0,0,0,0.2);
  }
  .customize-nav li.on a:after, .theme-selector-nav li.on a:after { /* right triangle replacer */
  content: "\25B6";
  display: block;
  float: right;
  margin: 0 5px 0 0;
  }
  .customize-nav li.on ul { /* sub ul menu container */
  padding: 5px 5px 5px 0!important;
  margin: 0 0 5px -5px!important;
  border-width: 0 0 2px 0!important;
  font-weight: bold!important;
  box-shadow: inset 0px 2px 5px -3px rgba(0,0,0,0.2);
  }
  .customize-nav li.on ul li { padding: 3px 0 3px 13px!important; } /* sub ul selected menu items padding */
  .customize-nav li, .customize-nav li a, .theme-selector-nav li a { border: 0!important; } /* remove right border from li */
  .customize-content .subheader.on { /* dropdown header on */
  cursor: default;
  margin: 0 0 10px 0;
  box-shadow: 0px 2px 5px -3px rgba(0,0,0,0.5);
  color: inherit;
  }
  .customize-content .subheader.on:before {
  content: "\25BC";
  display: block;
  float: left;
  margin: 0 10px 0 -12px;
  }
  .customize-content .subheader { /* dropdown header normal */
  cursor: default;
  margin: 0 0 10px 0;
  box-shadow: 0px 2px 5px -3px rgba(0,0,0,0.5);
  }
  .customize-content .subheader:before {
  content: "\25B6";
  display: block;
  float: left;
  margin: 0 12px 0 -12px;
  }
  .customize-content .moodtheme-preview, .layout-item.current { /* moodtheme preview container & selected page setup outer */
  border-radius: 8px;
  }
	
/* ==== MANAGE FRIENDS ==== */
.borderedtable th { border-width: 0 0 1px 0!important; }

/* ==== JOURNAL STATISTICS & SEARCH RESULTS ==== */
ul.pages-list.pages-list-statistics li, .pages-list li { /* active large gradient tab */
border-width: 0 0 3px 0!important;
}
ul.pages-list.pages-list-statistics li.cur, ul.pages-list.pages-list-statistics li:hover,
.pages-list .cur, .pages-list .cur-last, .pages-list li:hover { /* inactive large gradient tab and tab hover */
box-shadow: 0 -2px  5px -5px rgba(0,0,0,0.6);
}
.search-full { border-width: 0 0 0 3px!important; } /* expanded post */
.pages-list li strong, .pages-list li a { border-right: 0!important; }

/* ==== INBOX ==== */
.inbox .header { padding: 5px 0 5px 0!important; } /* actions header */
.inbox .header .actions { padding: 5px 0 5px 0!important; } /* actions header padding */
.folders a:visited, .folders a:link { /* folders padding */
padding-top: 5px!important;
padding-bottom: 5px!important;
padding-right: 5px!important;
border-radius: 8px;
}
.NotificationTable td { border-width: 1px 0 0 0!important; }
.NotificationTable .inbox { border: 0!important; }
td[style*="border-left: 1px solid #ccc"] { border-width: 0 0 0 1px!important; }

/* ==== UNSTYLIZED COMMENT PAGES ==== */
.b-updateform-buttons-firstsection { border-width: 0 1px 0 0!important; } /* reply box top divider */
.b-singlepost > div:first-child { /* author info container */
position: relative;
width: 100%;
left: -10px;
margin: 0 0 10px 0;
padding: 0 10px 0 10px;
border-width: 0 0 1px 0!important;
}
.b-singlepost-author-userinfo { /* author info */
font-weight: bold!important;
}
.b-linkbar { /* post action bar */
border-radius: 8px;
}
.b-linkbar .b-linkbar-item:hover { opacity: 0.8; }

/* ~ post comment main container */
.b-xylem { 
margin: 0 auto 10px auto!Important;
border-width: 1px 0 1px 0!important;
padding: 10px 30px 10px 30px!important;
}
