}
.b-leaf-actions { padding: 0 10px 5px 10px!important; }
.b-singlepost .b-leaf-actions .active A:link, .b-singlepost .b-leaf-actions .active A:visited,
.b-singlepost .b-leaf-actions .active A:hover, .b-singlepost .b-leaf-actions .active A:active { /* comment actions reply / expand */
