style.css:
@-moz-document url-prefix("http://www.nicovideo.jp/watch"){

.size_medium #playerContainer #playerNicoplayer:after{
 content:" ";
 height: 12px;
 width: 672px;
 background-color: #000000;
 position: absolute;
 left:0;
 top:425px;
}

.size_medium #playerContainer:after{
 content:" ";
 height: 10px;
 width: 31px;
 background-color: #161616;
 background-image: linear-gradient(#1a1a1a, #161616);
 position: absolute;
 left:155px;
 top:448px;
}

.size_normal #playerContainer #playerNicoplayer:after{
 content:" ";
 height: 12px;
 width: 898px;
 background-color: #000000;
 position: absolute;
 left:0;
 top:521px;
}
}

@-moz-document url-prefix("http://live.nicovideo.jp/watch/lv"){
#watch_player_bottom_box .box_inner{position:relative;}

#watch_player_bottom_box .box_inner #watch_like_buttons:before{
 content:" ";
 height: 31px;
 width: 373px;
 background-color: #8E8E8E;
 position: absolute;
 left:153px;
 top:-45px;
}

#watch_player_bottom_box .box_inner #watch_like_buttons:after{
 content:" ";
 height: 31px;
 width: 123px;
 background-color: #8E8E8E;
 position: absolute;
 left:5px;
 top:-45px;
}
}
