style.css:
@-moz-document url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/settings"), url-prefix("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/followers"), url-prefix("https://www.tumblr.com/reblog"), url-prefix("https://www.tumblr.com/new"), url-prefix("https://www.tumblr.com/liked"), url-prefix("https://www.tumblr.com/edit"), url-prefix("https://www.tumblr.com/tagged"), url-prefix("http://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/inbox"), url-prefix("http://www.tumblr.com/settings"), url-prefix("http://www.tumblr.com/likes"), url-prefix("http://www.tumblr.com/blog"), url-prefix("http://www.tumblr.com/followers"), url-prefix("http://www.tumblr.com/reblog"), url-prefix("http://www.tumblr.com/new"), url-prefix("http://www.tumblr.com/liked"), url-prefix("http://www.tumblr.com/edit"), url-prefix("http://www.tumblr.com/tagged") {

.l-content {

background:none!important;

}body.logged_in, body.logged_out {

background: url('http://i49.tinypic.com/dbqj9t.jpg')!important;
background-color:#0e0f13!important;
background-position:bottom left!important;
background-repeat:no-repeat!important;
background-attachment:fixed!important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-color:rgba(215,206,199,0.0)!important;
-webkit-border-radius:20px;
-moz-border-radius:20px;
border-radius:20px;
margin:auto;
}
  
  .l-header-container--refresh .l-header {background-color:#0e0f13!important;}

#posts .notification.first_notification .nipple.border,#posts .notification_alt .nipple.border,#posts .notification .nipple.border {
border:none!important;
}

#posts .avatar_frame {
border:1px #868686 solid!important;
}

.notification.single_notification A,.notification.first_notification A,.notification A,.notification.last_notification A,.notification .hide_overflow A {
border-bottom:solid thin #868686!important;
}

.notification.single_notification,.notification.first_notification,.notification.last_notification,.notification,.avatar_frame,.answer_container,.private_label {
background-color:rgba(64,64,64,.15)!important;
border-right:solid thin rgba(32,32,32,.65)!important;
border-bottom:solid thin rgba(32,32,32,1)!important;
border-left:solid thin rgba(32,32,32,.65)!important;
border-top:solid thin rgba(32,32,32,.65)!important;
}

.no_posts_found,h1.dashboard_header {
color:#333;
}

img#content_top,img#content_bottom {
display:none;
}

#right_column .dashboard_nav_item {
background-attachment:initial;
background-position-x:50%;
background-position-y:100%;
background-origin:initial;
background-clip:initial;
background-color:initial;
}

#right_column .dashboard_nav_item #hide_radar {
background-repeat-x:no-repeat;
background-repeat-y:repeat;
background-attachment:initial;
background-position-x:0;
background-position-y:0;
background-origin:initial;
background-clip:initial;
background-color:#333;
}

ol#posts li.notification {
background-color:rgba(0,0,0,0.5);
border-bottom:1px solid #000;
}

ol#posts li.notification.last_notification {
border-bottom:1px solid #000;
}

ol#posts li.post .permalink {
background:rgba(00,0,0,0.5);
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
background-color:#0e0f13!important;
border:solid #000;
}

ol#posts li.post .post_avatar {
border-bottom:0 solid #000;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
border-top:1px solid #000;
border-bottom:1px solid #000;
}

form.dashboard_options_form {
background-color:rgba(0,0,0,0.0);
border-bottom:1px solid #000;
border-bottom-left-radius:10px 10px;
border-bottom-right-radius:10px 10px;
border-top-left-radius:10px 10px;
border-top-right-radius:10px 10px;
color:#000;
display:block;
font-size:13px;
margin-bottom:25px;
padding:15px 22px;
}

form.dashboard_options_form .option_container {
border-top:1px solid #000;
}

#nav .nav_item.active,#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background:rgba(00,0,0,0.5)!important;
}

ol#posts li.notification.alt,ol#posts li.notification.first_notification {
background-color:rgba(0,0,0,0.5);
}

.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]) {
background:none!important;
width:0!important;
height:0!important;
margin-top:0!important;
border-bottom:solid 11px rgba(32,32,32,1)!important;
border-right:11px solid transparent!important;
border-left:11px solid transparent!important;
}

.selection_nipple:not([class="selection_nipple white"]) {
border-bottom:solid 11px rgba(32,32,32,1)!important;
}

.nipple {
background:none!important;
width:0!important;
height:0!important;
border-right:solid 6px rgba(16,16,16,.75)!important;
border-bottom:6px solid transparent!important;
border-left:0 solid transparent!important;
border-top:6px solid transparent!important;
margin-top:0!important;
margin-left:0!important;
}

.nipple.border {
background:none!important;
width:0!important;
height:0!important;
border-right:solid 6px rgba(32,32,32,.65)!important;
border-bottom:6px solid transparent!important;
border-left:0 solid transparent!important;
border-top:6px solid transparent!important;
margin-top:1px!important;
margin-left:0!important;
}

.notification_type_icon,#right_column::after {
display:none!important;
}

li.selected,li.recessed,a.active,div.post_filter_tabs a,.content.is_media,.mask.top,.mask.bottom,div#right_column,div#right_column:after,#help_search {
background:none!important;
}

div.l-content #tracked_tags.controls_section {
border: none !important;
}

div.l-content #tracked_tags.controls_section a.tag:hover {
background-color: #1e1e1e !important;
}

div.l-content #tracked_tags.controls_section a.tag span.count, div.l-content .tracked_tag .remove_tag, div.l-content .tracked_tag .hide_overflow {
color: #ffffff !important;
}

img#content_bottom.content_cap,
img#content_top.content_cap {
display: none !important;
}

.post_avatar_link, .post_avatar {
background-color:transparent!important;
}
}
