style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.steampowered.com") {

.alt1, .alt1Active, .alt2, .alt2Active, .tfoot { background:#31312f !important; border-color:#000 !important; color:#C5CAB6 !important; }

.thead { border-color:#000 !important; }

.tcat { font-weight:normal !important; }

.vbmenu_control a:link, .vbmenu_control_alink, .vbmenu_control a:visited, .vbmenu_control_avisited { color:#adadac !important; }

#content, li { font-family:Calibri,Verdana,Arial,Helvetica,sans-serif !important; font-size-adjust:.6 !important; }

li { font-size:1em !important; line-height:1.5em !important; }

ol>li { list-style-type:decimal !important; }

}
