style.css:
/* Created by Nuked  22-02-2020 */
@-moz-document url-prefix("https://multiplayer.it/") {
    :root {
    --main-bg-color:/*[[colorx]]*/;
    }
    
    .header__nav ul>li>a .glyphicon {
    color:#fff!important;
    font-size:13px;
    }
    
    .nav>li>a:hover,.nav>li>a:focus {
    background-color:#df1111;
    text-decoration:none;
    }
    
    .header__nav ul>li.dropdown .dropdown-menu--profile .wrapper-link-bottom a:hover,.header__nav ul>li.dropdown .dropdown-menu--profile .wrapper-link-top a:hover {
    background-color:#0000002e;
    }
    
    .header__nav ul>li#user_nav .wrapper-notifications ul li:before {
    border:solid 1px #f7f3f3;
    }
    .ui-menu-item:hover  {
    color:#fff !important;
    } 
    
    body {
    color:#fafafa;
    }
    
    html body {
    background-color:#000;
    }
    
    html,body {
    background:#000!important;
    }
    
    .comments .comments__comment .comments__comment__title__data .score:hover:after {
    background-color:var(--main-bg-color);
    opacity:0.7;
    }
    .emojionearea .emojionearea-editor {

    color: #fff;
}
    .hero-news__news__link-bottom {
    border:1px solid var(--main-bg-color)!important;
    filter:blur(0px);
    }
    
    .media figure {
    border:solid 1px var(--main-bg-color)!important;
    }
    
    .extra .comments:after {
    border-color:transparent var(--main-bg-color) transparent transparent !important;
    }
    
    .header__nav .dropdown .dropdown-menu {
    -moz-box-shadow:0 1px 6px rgba(38,38,38,.2);
    -webkit-box-shadow:0 1px 6px rgba(38,38,38,.2);
    border-top:solid 3px var(--main-bg-color)!important;
    box-shadow:0 1px 6px rgba(38,38,38,.2);
    color:#F0F8FF;
    }
    
    .article__body .list--source li a {
    border-bottom:solid 1px var(--main-bg-color);
    border-left:solid 1px var(--main-bg-color);
    }
    
    .right-bar .box .box__title h4 {
    color:#FFF!important;
    text-shadow:0 0 5px #fff, 0 0 3px #fff, 0 0 0 #fff, 0 0 4px var(--main-bg-color), 0 0 1px var(--main-bg-color), 0 0 1px var(--main-bg-color), 0 0 1px var(--main-bg-color), 0 0 0 var(--main-bg-color);
    }
    
    .header__nav ul>li#user_nav .notifications__avatar .numb {
    border:solid 1px var(--main-bg-color)!important;
    color:var(--main-bg-color)!important;
    }
    
    .comments .comments__comment {
    border-top:solid 1px var(--main-bg-color)!important;
    }
    
.wrapper-total {
    
    background: #101010;
}
    .form-control,.emojionearea,.emojionearea.form-control {
    background-color:#101010;
    border:1px solid var(--main-bg-color)!important;
    color:#fff;
    }
    
    ::placeholder {
    color:#FFF!important;
    }
    
    .right-bar .box {
    background:#101010!important;
    border-bottom:solid 0 #2f2f2f!important;
    border-left:solid 0 #2f2f2f!important;
    border-right:solid 0 #2f2f2f!important;
    border-top:solid 0 #2f2f2f!important;
    }
    
    .form-group input:focus,.form-group select:focus,.form-group textarea:focus,.list-profile li:hover .badge {
    border-color:var(--main-bg-color);
    }
    
    .btn-orange-phantom-multi svg,.footer .footer__brand svg circle {
    fill:var(--main-bg-color);
    }
    
    .btn-orange-phantom-multi {
    background-color:#fff!important;
    border-color:var(--main-bg-color)!important;
    border-width:2px!important;
    color:var(--main-bg-color)!important;
    font-weight:400!important;
    text-transform:uppercase!important;
    }
    
    .btn-orange-phantom-multi:active:focus,.btn-orange-phantom-multi:active,.btn-orange-phantom-multi:focus,.btn-orange-phantom-multi:hover,.box-game-card-tag--small .media .btn-group-gamecard .btn.btn-voto:hover,.live .live__calendar,.live .live__calendar p small {
    background-color:var(--main-bg-color)!important;
    border-color:var(--main-bg-color)!important;
    border-radius:4px;
    color:#fff!important;
    outline:none;
    }
    
    .article__header h1 .article__header__comments a:after {
    border-color:transparent transparent transparent var(--main-bg-color) !important;
    }
    
    .live .live__title {
    border-bottom:solid 3px var(--main-bg-color)!important;
    }
    
    .media--standard:after {
    background-color:#f0f8ff00;
    opacity:0.3;
       border:none;
    }
    
    .flow-news.flow-news--small {
    border-left:0;
    }
    
    .btn-orange-multi:active:focus,.btn-orange-multi:active,.btn-orange-multi:focus,.btn-orange-multi:hover {
    background-color:var(--main-bg-color)!important;
    border-color:var(--main-bg-color)!important;
    color:#fff!important;
    opacity:0.7;
    outline:none;
    }
    
    .header__brand a svg circle {
    fill:#fff;
    }
    
    .live .live__header .live__header__left i {
    color:#161616;
    }
    
    .header__nav ul>li.search:hover {
    background-color:#0000;
    }
    
    .btn-orange-multi {
    background-color:var(--main-bg-color)!important;
    border-color:var(--main-bg-color)!important;
    color:#fff!important;
    text-transform:uppercase!important;
    }
    
    .header__nav ul>li.dropdown .dropdown-menu--news .dropdown-menu--news__item:hover {
    background-color:#ffffff2e;
    }
    
    .header__nav .dropdown .dropdown-menu--news .dropdown-menu--news__all {
    border-bottom:solid 0 #eee;
    color:#fff;
    }
    
    .header__nav ul>li.dropdown .dropdown-menu--news .dropdown-menu--news__all:hover {
    background-color:#eeeeee14;
    color:#fff;
    text-decoration:none;
    }
    
    .nav-tabs>li.active>a,.nav-tabs>li.active>a:hover,.nav-tabs>li.active>a:focus {
    background-color:var(--main-bg-color);
    color:#fff;
    }
    
    .header__nav ul>li.dropdown .dropdown-menu--reviews .dropdown-menu--reviews__all:hover {
    background-color:#ffffff26;
    color:#fff;
    text-decoration:none;
    }
    
    .header__nav .dropdown .dropdown-menu--reviews .dropdown-menu--reviews__item .dropdown-menu--reviews__item__title .platform {
    background:#ffffff7d;
    border-radius:5px;
    color:var(--main-bg-color);
    font-weight:bold;
    letter-spacing:0;
    padding:2px;
    }
    
    .header__nav .dropdown .dropdown-menu--reviews .dropdown-menu--reviews__item .dropdown-menu--reviews__item__vote strong,.header__nav .dropdown .dropdown-menu--reviews .dropdown-menu--reviews__item .dropdown-menu--reviews__item__recommended strong,.header__nav .dropdown .dropdown-menu--reviews .dropdown-menu--reviews__item .dropdown-menu--reviews__item__deprecated strong {
    color:#fff;
    display:block;
    font-size:30px;
    line-height:.8;
    }
    
    .nav-tabs>li.active {
    border:none;
    border-bottom:solid 2px var(--main-bg-color);
    }
    
    .hidden-sm a:hover,.notifications__avatar:hover,.dropdown:hover .dropdown-toggle,.header__nav ul>li.focus>a,.header__nav ul>li.open>a {
    background-color:#0000001a!important;
    }
    
    .header__nav ul>li.dropdown .dropdown-menu--reviews .dropdown-menu--reviews__item:hover {
    background-color:#ffffff21;
    }
    
    .header__nav .dropdown .dropdown-menu--reviews .dropdown-menu--reviews__all {
    border-bottom:solid 0 #2d2d2d;
    color:#fff;
    }
    
    .header__nav ul>li.dropdown .dropdown-menu--platform .platform a:hover {
    background-color:#eeeeee61;
    color:#232323;
    text-decoration:none;
    }
    
    .header__nav .dropdown .dropdown-menu--platform .platform a {
    color:#fff;
    display:block;
    padding:3px 0;
    }
    
    #MULTIPLAYER {
    fill:#FFF;
    }
    
    .hero-news__news__link-bottom:hover {
    filter:blur(0px);
    }
    
    .container {
    background-color:#000;
    margin-top:29px;
    }
    
    .media a {
    color:#fbfbfb;
    transition:color .3s ease-in-out;
    }
    
    .container--home>.row {
    background:#101010;
    }
    
    #_article_container,
    .footer .container,
    .btn-orange-phantom-multi{
    background-color:#080808 !important;
    }
    
    .box--most .media.media--standard .media-body {
    float:left;
    max-width:calc(100% - 90px);
    }
    
    .flow-news.flow-news--big .media .media-body {
    max-width:calc(100% - 183px);
    }
    
    .media .media-left,.media--big .media-big {
    border-radius:0;
    }
    
    .hero-news__news {
    border:solid 0 var(--main-bg-color)!important;
    box-shadow:inset 0 0 1px 0 #fff, 0 0 0 var(--main-bg-color), 0 0 0 var(--main-bg-color), 0 0 0 var(--main-bg-color), 0 0 0 var(--main-bg-color), 0 0 0 var(--main-bg-color), 0 0 0 0 var(--main-bg-color), 0 0 0 var(--main-bg-color);
    filter:grayscale(100%);
    transition:transform .2s, filter .5s ease-in-out,box-shadow .3s ease-in-out;
    }
    
    .hero-news__news:hover,.hero-news__news--first:first-child {
    border:solid 0 var(--main-bg-color)!important;
    box-shadow:inset 0 0 1px 0 #fff, 0 0 10px var(--main-bg-color), 0 0 0 var(--main-bg-color), 0 0 9px var(--main-bg-color), 0 0 0 var(--main-bg-color), 0 0 3px var(--main-bg-color), 0 0 2px 0 var(--main-bg-color), 0 0 3px var(--main-bg-color);
    filter:grayscale(0);
    transform:scale(1.1);
    z-index:999;
    }
    
    .media figure,.box--releases__item__link,.box--promo a {
    border:solid 0 var(--main-bg-color)!important;
    box-shadow:inset 0 0 3px 0 #fff, 0 0 3px #fff, 0 0 0 var(--main-bg-color), 0 0 0 var(--main-bg-color), 0 0 0 var(--main-bg-color), 0 0 0 var(--main-bg-color), 0 0 2px 0 var(--main-bg-color), 0 0 0 var(--main-bg-color);
    margin-left:9px;
    margin-top:8px;
    transition:box-shadow .3s ease-in-out;
    }
    
    .media:hover figure,.box--releases__item__link:hover,.box--promo a:hover {
    border:solid 0 var(--main-bg-color)!important;
    box-shadow:inset 0 0 1px 0 #fff, 0 0 10px var(--main-bg-color), 0 0 0 var(--main-bg-color), 0 0 9px var(--main-bg-color), 0 0 0 var(--main-bg-color), 0 0 3px var(--main-bg-color), 0 0 2px 0 var(--main-bg-color), 0 0 3px var(--main-bg-color);
    margin-left:9px;
    margin-top:8px;
    }
    
    .media:hover a{
    color:var(--main-bg-color)!important;
    }
    
    .media--big .media-big {
    padding-bottom:54.3%;
    }
    
    .media--big .media-body {
    padding-left:0;
    }
    
    .media .media-body {
    margin-left:7px;
    margin-top:6px;
    }
    
    .media .media-body header {
    margin-left:7px;
    }
    
    .right-bar .box.box--releases .box--releases__item .box--releases__item__link {
    margin:8px;
    }
    
    html body ::selection {
    background-color:#0000006e!important;
    color:#fff;
    }
    
    .right-bar .box.box--releases .box--releases__item .box--releases__item__img {
    background-color:transparent;
    background-position:center center;
    background-size:cover;
    bottom:0;
    left:0;
    margin:8px;
    overflow:hidden;
    position:absolute;
    right:0;
    top:0;
    z-index:1;
    }
    
    .right-bar .box.box--releases .box--releases__item .box--releases__item__gradient {
    background-color:#0000001c!important;
    margin:8px;
    }
    
    .container--home .col-news-left:after,.container--home .col-news-right .col-news-right__news:after {
    background:none!important;
    }
    
