style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {
/*Gmail - Setting, Help, and Sign-out Buttons*/
td[class="trb"] a[target="_top"],
td[class="ml"] a[target="_top"], #prf_g, #hlp {
padding: 1px 15px 1px 15px;
color: rgb(17,37,55) !important;
text-decoration:none !important;
background-color:rgb(195,217,255) !important;
border: 1px solid rgb(51,102,153);
border-left-color:rgb(232,238,247);
border-top-color:rgb(232,238,247);
}
td[class="trb"] a[target="_top"]:hover,
td[class="ml"] a[target="_top"]:hover, #prf_g:hover, #hlp:hover {
border: 1px solid rgb(232,238,247);
border-left-color:rgb(51,102,153);
border-top-color:rgb(51,102,153);
}
td[class="trb"] a[target="_top"]:active,
td[class="ml"] a[target="_top"]:active, #prf_g:active, #hlp:active {
padding: 2px 14px 0 16px;
}
}
