options.value:
@-moz-document url-prefix(http://www.nicovideo.jp/){

/*= ヘッダを統合する =*/
/*全体*/
.siteHeaderGlovalNavigation{
	position: absolute;
	left: 400px;
}

/*ニコニコTOP*/
#header h1,
#header > div{
	position: fixed !important;
	z-index: 9999 !important;
}
.nofix #header h1,
.nofix #header > div{
	position: absolute !important;
	z-index: 9999 !important;
}
#header > *{
	top: -5px;
	left: 25px;
}
#header > .headerIcon{
	top: 0;
	left: 0;
	width: 1px !important;
	padding-top: 3px !important;
	padding-left: 10px !important;
}
#header h1#logo5th{
	padding-top: 4px !important;
}
body > div[style*="relative;"]{
	position: static !important;
}
#navigations{
	width: 100% !important;
}
.logo_sp_banner{
	top: 32px !important;
	left: 10px !important;
}
#header{
	padding-top: 42px !important;
}
.nofix #header{
	padding-top: 16px !important;
}

/*ニコニコ動画*/
#PAGEHEADER,
#head_logo > *,
#head_search,
#search_tab,
#search_input,
#search_input p,
#search_input input,
#head_search_form,
#head_search_form > table{
	position: fixed !important;
}
.nofix #PAGEHEADER,
.nofix #head_logo > *,
.nofix #head_search,
.nofix #search_tab,
.nofix #search_input,
.nofix #search_input p,
.nofix #search_input input,
.nofix #head_search_form,
.nofix #head_search_form > table{
	position: absolute !important;
}
#PAGEHEADER{
	top: 0;
	left: 0;
	width: 1px !important;
	z-index: 9999 !important;
}

/*ニコニコ動画 - 検索バー*/
#head_search{
	top: 0;
	left: 0;
}
#head_search_form{
	top: 0;
	left: 105px;
}
#search_input{
	width: 235px !important;
	top: 0px;
	left:  105px;
}
.nofix #search_input{
	left:  0px;
}
#search_input p,
#search_input input{
	height: 18px !important;
	padding: 0 !important;
}
#search_input > .typeText{
	top: 3px;
	left: 105px;
}
.nofix #search_input > .typeText{
	left: 0px;
}
#search_input > .typeSubmit,
#search_input > .typeSubmit > input{
	width: 16px !important;
}
#search_input > .typeSubmit{
	top: 6px;
	left: 315px;
}
.nofix #search_input > .typeSubmit{
	left: 210px;
}

/*ニコニコ動画 - 検索タイプ*/
#search_tab{
	width: 55px;
	top: 5px;
	left: 340px;
}
.nofix #search_tab{
	left: 235px;
}
#search_tab a{
	width: 5px !important;
}

/*ニコニコ動画 - ロゴ*/
#head_logo p{
	padding: 0 !important;
}
#head_logo #hidariue{
	left: 10px;
	top: 9px;
}
#head_logo #nicodou{
	left: 25px;
	top: 6px;
	width: auto !important;
	height: auto !important;
}
#siteHeaderInner{
	width: auto !important;
	max-width: none !important;
	padding: 0 20px 0 5px !important;
	height: 26px !important;
}
#siteHeaderInner .siteHeaderGlovalNavigation,
#siteHeaderInner .siteHeaderMenuList{
	padding-top: 0 !important;
}
#PAGEBODY{
	padding-top: 7px !important;
}
.nofix #PAGEBODY{
	padding-top: 17px !important;
}
/*= ここまで =*/

/*= ヘッダを統合用に改変 =*/
/*ヘッダ背景*/
#siteHeader{
	background: rgba(60,60,60, 0.9) !important;
	box-shadow: -3px 0 3px #555;
}
#siteHeaderInner{
	background: none !important;
}

/*ヘッダーリンク*/
#siteHeaderInner > ul > li{
	height: 26px !important;
	line-height: 24px !important;
}
#siteHeaderInner > ul > li:hover{
	background: rgba(0, 0, 0,  0.3) !important;
}
#siteHeaderInner > ul > li > a,
#siteHeaderInner > ul > li > a > span{
	border: none !important;
	text-decoration: none !important;
}
#siteHeaderUserNickNameContainer{
	font-size: 11px !important;
}
.siteHeaderNotNotifications{
	margin: 0 5px 1px 0px !important;
}
#siteHeaderInner > ul > li > a{
	color: #fff !important;
	font-size: 10px !important;
}

/*ドロップダウンメニュー*/
#siteHeaderInner > ul > li > ul{
	top: 25px !important;
	border: 1px solid #aaa !important;
	padding-bottom: 0 !important;
	background-color: rgba(245,245,245, 0.93) !important;
}
#siteHeaderInner > ul > li > ul > li,
#siteHeaderInner > ul > li > ul > li > a{
	background: none !important;
	font-size: 11px !important;
	height: 26px !important;
	color: #333 !important;
	overflow: hidden !important;
}
#siteHeaderInner > ul > li > ul >li > a:hover{
	background-color: #7a9da9 !important;
	color: #fff !important;
}
#siteHeaderRightMenuContainer > li:not(:last-child),
#siteHeaderLeftMenuContainer > li:not(:last-child):not(:nth-last-child(2)){
	border-bottom: 1px dotted #aaa !important;
}

/*他のサービスへのリンクをドロップダウンメニュー化する*/
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation *{
	display: none !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation:hover *{
	display: block !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation:before{
	content: "link";
	letter-spacing: 1px;
	padding: 0 4px;
	position: absolute;
	display: block;
	line-height: 14px;
	height: 14px;
	text-align: center;
	font-size: 9px !important;
	color: #fff;
	background-color: #777;
	box-shadow: 1px 1px 1px #444;
	border-radius: 3px;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation{
	top: 6px;
	height: 16px;
	width: 44px;
	border-radius: 3px;
	text-shadow: 1px 1px #444;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation:hover{
	text-shadow: 0 0 5px #eee !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li:first-child{
	margin-top: 15px !important;
}
#siteHeader #siteHeaderLeftMenuContainer{
	display: block !important;
	background: none !important;
	border: none !important;
	width: 201px !important;
	left: 102px !important;
	top: -136px !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > #siteHeaderLeftMenu.siteHeaderOther > a{
	display: none !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li,
#siteHeader #siteHeaderLeftMenuContainer > li{
	width: 100px !important;
	height: 26px !important;
}
#siteHeader #siteHeaderLeftMenuContainer > li{
	overflow: hidden !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li > a,
#siteHeader #siteHeaderLeftMenuContainer > li > a{
	width: 100px !important;
	height: 26px !important;
	color: #333 !important;
	font-size: 11px !important;
	line-height: 26px !important;
	padding-top: 0px !important;
	padding-bottom: 0px !important;
	text-shadow: none !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li > a:hover,
#siteHeader #siteHeaderLeftMenuContainer > li > a:hover{
	color: #fff !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li > a,
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li > a > span{
	border: none !important;
}

#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li{
	background-color: rgba(220,220,220, 0.93) !important;
}
#siteHeader #siteHeaderLeftMenuContainer > li{
	background-color: rgba(245,245,245, 0.93) !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li:not(.siteHeaderOther) > a:hover,
#siteHeader #siteHeaderLeftMenuContainer > li > a:hover{
	background-color: #7a9da9 !important;
}

#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li{
	border-left: 1px solid #aaa !important;
	border-right: 2px solid #ccc !important;
	border-bottom: 1px dotted #aaa !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li:first-child{
	border-top: 1px solid #aaa !important;
}
#siteHeader > #siteHeaderInner > .siteHeaderGlovalNavigation > li:last-child{
	border-bottom: 1px solid #aaa !important;
}
#siteHeader #siteHeaderLeftMenuContainer > li:nth-child(2n+1){
	border-right: 1px solid #ddd !important;
}
#siteHeader #siteHeaderLeftMenuContainer{
	border-style: solid !important;
	border-color: #aaa !important;
	border-width: 1px 1px 1px 0 !important;
}

/*タイトルロゴ*/
#header h1 > a:first-child:before{
	content: "ニコニコTOP";
	visibility: visible !important;
}
#header h1{
	width: 80px !important;
	height: auto !important;
	text-align: left !important;
	text-indent: 0 !important;
	overflow: hidden;
	background-image: none !important;
}
#header h1 > a:first-child{
	display: inline !important;
	float: none !important;
	color: #fff !important;
	text-decoration: none !important;
	font-weight: bold !important;
	font-size: 11px !important;
	white-space: nowrap;
	visibility: hidden;
}
#head_logo a[target="_top"] > img{
	display: none;
	font-size: 0 !important;
}
#head_logo a[target="_top"]:before{
	content: "ニコニコ動画";
}
#head_logo a[target="_top"]{
	display: block !important;
	color: #fff !important;
	text-decoration: none !important;
	font-weight: bold !important;
	font-size: 11px !important;
	white-space: nowrap;
}
#header h1 > a:hover,
#head_logo a[target="_top"]:hover{
	text-shadow: 0 0 3px #eee;
}

/*ランダムリンク*/
