style.css:
@-moz-document domain("zillow.com") {
@media only screen and (min-width: 1060px) {
.outer-border .generic-box {
    padding: 20px;
}

.generic-box .module-head {
    padding: 0 20px;
}

.header-fresh #c-column .module-body {
    padding: 1px 20px 20px 20px;
}

.header-fresh .module-body {
    padding: 1px 20px 20px 20px;
}

.header-fresh .generic-box .module-head, 
.header-fresh #content .generic-box .module-head {
    padding-top: 10px;
}

.b-c-layout #content {
    width: 690px;
}

.search-page #primary-nav-wrapper {
    width: auto;
}

.b-c-layout.liquid-layout #content {
    width: auto;
}

.a-b-c-layout #content {
    padding: 20px;
}


.a-b-c-layout #content .module-head, 
.a-b-c-layout #content .module-body {
   margin-left: 0;
   margin-right: 0;
   border-left: 0;
   border-right: 0;
   padding-left: 0;
   padding-right: 0;
}

.a-b-c-layout #content .module-body {

}

#c-column {
    width: 320px;
}

#header-wrapper, 
#primary-nav-wrapper, 
#container, 
#main-ft, 
#sub-ft, 
#hdp #link-footer {
    width: 1025px;
}

.generic-box.ad  {
   padding: 10px;
   background: #E9E9E9;
}


.pagination-2012 {
    padding-bottom: 33px;
}

/* Mortgage rates scrolling one */
#scrolling-rates .avg-rate {
    width: 140px;
}


/* Hdp specific */
.full-width-map #map-view {
    width: 650px;
}

#maps-and-views .module-foot .module-body {
    padding: 10px 20px !important;
}

.facebook-ad {
    display: none;
}
}
}
