style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("darkpony.ru") {

/* Скрытие рисунков, показ по наведению мыши */
#content .post p img {
    opacity: .05;
}
.avatar {
    opacity: .2;
}
#content .post p img:hover, .avatar:hover {
    opacity: .7;
}
/* Прячем шапку */
#header {
    background-image: none !important;
}
/* Прячем лишнее для SFW-режима */
.news-scroller, #sidebar {
    display: none;
}
.logo a[href="http://darkpony.ru"] {
    visibility: hidden;
}
.logo a[href="http://darkpony.ru"]:before {
    visibility: visible;
    content: "На_главную";
    color: #212121;
    text-decoration: underline;
}
body {
    background-image: none !important;
    background-color: #212121 !important;
}
#wrapper {
    background: #DEDEDE !important;
}

}
