style.css:
/*rein - improved tabs */

tab {
	margin: 0px !important;
	padding: 0px !important;
	background: none !important;
	border: none !important;
}
.tab-stack{
	margin: 2px 1px ;	
	padding: 1px 2px ;
	background: rgba(255,255,255,.6) ;
	border: 1px solid rgba(110,110,110,.6) ;
	border-radius: 3px ;
	background-clip: padding-box ;
	min-height: 24px ;
}
