style.css:
@-moz-document domain('wikia.com') { 
	/* Full width content */
	#WikiaPage { width: 100% !important; max-width: none !important; margin: 0px !important; padding: 0px  !important; }
	#PageHeader { padding: 16px 10px 12px; }
	#WikiaMainContent { width: 100% !important; margin: 0px !important; }
	#WikiaMainContentContainer { margin: 0px !important; }

	/* Remove wikia navigation */
	.wds-global-navigation-wrapper { height: 50px !important; }
	#globalNavigation { border: none !important; background: none !important; box-shadow: none !important; }
	.wds-global-navigation__logo, .wds-global-navigation__start-a-wiki { display: none !important; }
	.wds-is-games, .wds-is-movies, .wds-is-tv, .wds-global-navigation__wikis-menu { flex: 0 0 auto !important; display: none; }
	body { padding: 0px !important; }
	#WikiaFooter, .wds-global-footer, #WikiaBar { display: none !important; }

	/* Cinch up page, remove image frills */
	.WikiaPage { border: none !important; }
	.WikiaArticle { padding: 0 10px !important; }
	body.mediawiki { background-image:  none !important; }

	/* Remove ads */
	.wikia-ad { display: none !important; }
	#WikiaTopAds { display: none !important; }
	#WikiaArticleBottomAd { display: none !important; }
	#WikiaNotifications { display: none !important; }
}
