style.css:
@-moz-document domain("blog.esuteru.com") {
#header,#right_col,#headline,#top_tag_list,
#main_col hr,
.page_navi,
#footer,table,
.body:nth-child(7),.amazon_hot_item_entry,
#comment-form,[href="#text2"],
/*20160822*/
#ad_main_01,#ad_main_02,.bottom_area,.ranking_area,#bbs_list,
#form,.category_new_article_area,.article_detail_margin.relation_article_area
{display:none!important;}

.body{margin-top: 0px!important;
padding-top: 0px!important;
margin-bottom: auto!important;}



#main_col{padding-top: 0px!important;
float:left!important;
}
#main_col,
#entry{width:100%!important;}
.head{width:90%!important}


/*トップページ広告除外*/
.blog-title-6278,.plugin-twitter_follow_button,
.plugin-box.plugin-extra,.plugin-follow_buttons,
#index_top_ad,#article-1_5
{display:none!important;}
.blog-title-6287,#article_top_ad,#article_free1,#article_mid_upper,
#ad_related_articles_top_6289_71602,#article_bottom,.article-button-simple,
.plugin-blogroll,.image-item,.button-link,#lite_link,.sns_follow_area
{display:none!important;}

/*トップページ一年前の記事除外*/
.new_article_list_area:nth-of-type(3){display:none!important;}

.new_article_list_area{margin-top:0px!important;}
/*スマホ表示調整*/
#pagination{margin-bottom:2px;}
/*フッターすっきり*/
.plugin-free_area,.plugin-popular_articles-new,.plugin-category_archives,
.plugin-monthly_archives,.plugin-article_search,.blog-title-1643287,.footer-ranking,
.blog-footer,.footer_link_area
{display:none!important;}

/*SNSボタン非表示*/
.share_button_area_top.share_button_area,
.share_button_area_bottom.share_button_area{display:none!important;}

.wrapper{padding-top:0;}

.navi_area{display:none!important;}

.article_detail_area{margin-top:0;}

.last_margin{margin:0;}
}
