style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.flvto.com") {

body {background: #ddd !important;}

/* orange square container to grey */
.b_start  {background: #444 !important;}

/* Convert to MP3 caption */
.btn-conv {background: #252E35 !important;}
.btn-conv:hover {background: #476063 !important;}

/* text color of MP3 conversion caption */
.btn-conv, .jNiceSelectText {
color: #fff !important;
text-shadow: none !important;}

/* progress container */
.b_progress {width: auto !important;}

/* progress box */

.b_progress_info {
background: #444 !important; 
padding: 2em !important;}

/* big grey box when conversion begins */
.b_progress_status {
width: auto !important;
background: #444 !important;
position: static !important;}

/* Download button */
.dl-btn {

color:#fff !important;
background: #252E35 !important;
line-height: normal !important;
border: 2px solid #444 !important;
height: auto !important;
padding: 1em !important;}

.dl-btn:hover {background: #476063 !important;}

/* Video title */
body > div > h1[style] {
color: #eee !important;
width: auto !important;}

/* Convert Other Video */
a.u_other {

display: block !important;
width: auto !important;
height: auto !important;
padding: 1em 0 1em 0 !important;
position: static !important;
background: #333 !important;}

a.u_other:hover {background: #1D6787 !important;}


/* Remove */

#main > div:first-child ~ *,
.b1,
.b_share,
.b_social,
.b_what-conv,
.logo,
#link_url + label,
.progress_value + div,
#sliderbox,
.t_result > p,
.b_qr

{display: none !important;}

}
