@-moz-document url("chrome://speeddial/content/speeddial.xul"){

.speeddial-container {
    border-radius: 3px !important;
    outline: rgba(10,10,10,0.0) 1px solid !important;
}

.thumbnail{
   opacity: 0.8 !important; 
   margin:  0px !important;
}

.thumbnail:hover  {
  opacity: 1 !important; 
}

.speeddial-container { 
    background-color:  transparent  !important;
 
 }

.thumbnail-title,
.speeddial-top-middle,
.speeddial-bottom-middle,
.speeddial-top-middle,
.speeddial-middle-left,
.speeddial-middle-right,
.speeddial-bottom-middle,
.speeddial-top-left,
.speeddial-top-right,
.speeddial-bottom-left,
.speeddial-bottom-right{
display: none;
}
.speeddial-container {
    margin: 0 !important;
    border: 0 !important;
}
}
