style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vk.com") {

    #pv_like_link:after {
        background: #FFFFFF;
        color: #2F5879;
        content: "Me gusta";
        display: block;
        position: absolute;
        top: 0;
        width: 100%;
    }
    
    #pv_like_link {
        color: #FFFFFF !important;
        position: relative !important;
        width: 48px !important;
        overflow: hidden !important;
        white-space: nowrap !important;
    }

    .post_like_link:after {
        background: #FFF;
        color: #2F5879;
        content: "Me gusta";
        display: block;
        position: absolute;
        top: 0;
        width: 100%;
    }
    .wall_module .post_like:hover .post_like_link:after {
        background: #E9EDF1 !important;
    }
    .post_like_link {
        color: #FFFFFF !important;
        position: relative !important;
        width: 41px !important;
        overflow: hidden !important;
        white-space: nowrap !important;
    }
    .page_wide_no_narrow .post_like_link {
        width: 44px !important;
    }

    .like_link:after {
        background: #FFF;
        color: #2F5879;
        content: "Me gusta";
        display: block;
        position: absolute;
        top: 0;
        width: 100%;
    }
    .like_link {
        color: #FFFFFF !important;
        position: relative !important;
        width: 41px !important;
        overflow: hidden !important;
        white-space: nowrap !important;
    }
    .page_wide_no_narrow .like_link {
        width: 44px !important;
    }
}
