style.css:
@-moz-document domain("tumblr.com") {
#dashboard_index, #dashboard_inbox, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_likes, #dashboard_followers, #dashboard_following, #dashboard_directory, #dashboard_lookup_contacts, #main_doc_page, #recommended_tumblelogs, #dashboard_settings, #dashboard_create_tumblelog, #dashboard_members, #main_meetups,
#main_about, #main_explore, #dashboard_goodies, #main_developers, #main_themes, #dashboard_manage_themes, #main_edit_theme, #main_jobs, #main_policy, #tagged_display, #dashboard_tumblelog_settings {
background: url(/*[[myurl]]*/) repeat top left fixed !important;}

.plexi {
background: rgba(0,0,0,0.5) !important;
}


#dashboard_index div.l-content,
#dashboard_inbox div.l-content,
#dashboard_submissions div.l-content,
#dashboard_drafts div.l-content,
#dashboard_post_queue div.l-content,
#dashboard_likes div.l-content,
#dashboard_followers div.l-content,
#dashboard_members div.l-content,
#tagged_display div.l-content,
#main_tag div.l-content,
#dashboard_tumblelog_settings #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgba(153, 134, 117, 0.0) !important }

#left_column #posts .notification .block {
  color: transparent !important;}

#left_column #posts .notification.alt {
  background-color: rgba(31,31,31,.75) !important;}

#left_column #posts .notification.last_notification,
#left_column #posts .notification.single_notification {
  border-bottom-color: transparent !important;}

#left_column #posts .notification a {
  color:#ffffff !important;
  border-bottom:0px !important;}

#left_column #posts .notification blockquote {
  border-left-color:transparent !important;}

#left_column #posts .notification .avatar {
  border-color: transparent !important;
  background-color:transparent !important;
  opacity:1 !important;}

#left_column #posts .notification .avatar:hover {
  opacity:1 !important;}

.selection_nipple { border-bottom-color: rgba(0,0,0,0.1) !important }

.permalink { background: none !important }

.avatar_frame {background-color: rgba(255,255,255,0.1) !important; border-color: rgba(255,255,255,0.1) !important }

.notification {background: rgba(31,31,31,.90) !important;
border: rgba(31,31,31, 150) !important; background-image: none !important; margin-bottom: 1px !important }

.notification a { border-bottom: 1px solid rgba(0,0,0,0.1) !important }

.notification a:hover { border-bottom: 1px solid rgba(0,0,0,0.3) !important }

.notification_type_icon { display: none !important }

.nipple { border-right-color: rgba(0,0,0,0.07) !important }


#new_post_notice_container.tab_notice {
box-shadow: none !important;
color: #ffffff !important;
background-color: rgba(31,31,31,.75) !important;
letter-spacing: 1px !important;
border-radius: 3px !important;
-webkit-border-radius: 3px !important;
border: solid 1px rgba(31,31,31,1) !important;
line-height: 12px !important;
padding: 3px !important;
}

#dashboard span.tab_notice_value {
color: #000000 !important;
}

#container .selection_nipple, #container .tab_notice_nipple {
visibility: hidden;
}

#recommended_tumblelogs {
background:rgba(31,31,31, 255) !important}

.with_subtitle small { color: rgba(0,0,0,0.5) !important }

.recessed a { color: rgba(255,255,255,0.7) !important }

div#right_column,
div#right_column:after {
  background: none !important;}

* { text-shadow: none !important }

h1, .title { color: rgba(255,255,255,0.6) !important }

.no_posts_found, h1.dashboard_header { color: rgba(255,255,255,0.9) !important }

.dashboard_options_form {background: rgba(0,0,0,0.1) !important; border: none !important; color: rgba(31,31,31,0.7) !important}

.dashboard_options_form_disclaimer, .dashboard_options_form_disclaimer a { color: rgba(255,255,255,0.9) !important }

.blue_note, .blue_note a {background: rgba(0,0,0,0.2) !important; color: rgba(0,0,0,0.6) !important }

div.l-content #right_column li {
background-color: rgba(31,31,31,.5) !important;
}

div.l-content #right_column li:hover {
box-shadow: rgba(255,255,255,0.05)  5px 0px 15px inset !important;
}

div.l-content #right_column::after {
background: none !important;
}

div.l-content #right_column .small_links a{
color: rgba(31,31,31,.3) !important;
}

div.l-content #right_column a, div.l-content .publish_on_day, div.l-content .publish_on_time, div.l-content #right_column small,div.l-content #right_column .link_arrow, div.l-content #right_column #tumblr_radar .radar_footer span, div.l-content #right_column .count, div.l-content #right_column .mark_date, div.l-content a.username, div.l-content #page_heading {
color: #ffffff !important;
}

#footer, #footer a {border: none !important; color: rgba(0,0,0,0.5) !important }

ol#posts li.post.new_post { }

img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{width:0; height:100px; padding-right:250px; opacity: 0;}

.new_post_label {-webkit-text-stroke:0 !important; color: rgba(0,0,0,0.4) !important; opacity:1 !important; top: 0px !important; padding-top: 80px }

.new_post_label:hover { color: rgba(0,0,0,0.7) !important }

#pagination a {background: rgba(0,0,0,0.4) !important;
color: rgba(255,255,255,0.7) !important }

#pagination a:hover {color: rgba(255,255,255,0.9) !important }

#dashboard_controls a, #dashboard_controls .control {color: rgba(31,31,31,9) !important }

.dashboard_controls_arrow_first_page, .dashboard_controls_arrow_left, .dashboard_controls_arrow_right {display: none !important }

#auto_pagination_loader {background: transparent!important; border: 0px!important; font-weight: normal; font-family: Impact, serif; color: white; }

#logo {
height: 100px !important;
width: 295px !important;
margin-top: 10px !important;
}

#logo > .logo_anchor {
background-image: url('http://i47.tinypic.com/1fwrrt.png') !important;
}


#tumblr_radar, .radar_content, .radar_controls, .radar_footer {
display: none;
opacity: 0;
text-indent: -9999px;
}

#return_to_top .return_to_top_icon {
background: url('http://data.sainet.co.uk/medigel/scroll_to_top/scroll-to-top_standard-1.png') no-repeat !important;
}
}
