style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("bing.com") {

body {
counter-reset: result !important;
min-width: 700px !important;
}

#results h3:before {
content: counter(result) ". " !important;
counter-increment: result !important;
color: #000 !important;
}
  
#results_area{
width: 100% !important;
}

.sw_hdr_img, .sb_adsNv2, #sidebar{
display: none !important;
}

/*#sb_form_q {
width: 500px !important;
}*/

.sa_wr strong {
border-bottom: 1px dashed yellowgreen !important;
}
  
.sa_wr cite {
color:#e4892a !important;
font-weight: normal !important;
}

.sb_meta {
color: red !important;
font-weight: bold !important;
}
    
.sa_wr p {
line-height:1.0em !important;
}
 
#wg0 > li:nth-child(2n + 1) {
background-color: #EEEEFF !important;
}

a:hover {
background: #ff6 !important;
color: black !important;
}

a:active {
color: #008000 !important;
text-decoration: underline !important;
}

.sb_count {
color:olive !important;
font-weight: bold !important;
}

.ftrH {
border:solid #DDCC88 1px !important;
background-color: #FFFFDD !important;
}

#vidans2 {
border: dashed tomato 1px !important;
background-color: #C6E2D6 !important;
}

.ans2.ans_n2 {
min-width: 100% !important;
border: dashed tomato 1px !important;
background-color: #F3D5B5 !important;
}

#results > ul > li:hover {
background-color: lightgoldenrodyellow !important;
}

.sw_qbox {
background-color: white !important;
}
 
.sa_sg.sa_hv {
background-color: #ff6 !important;
}

.sa_tm.sa_tmHS:before {
content: "(Search History) ";
font-size: x-small;
color: red;
}

/*
For multi-column, check this thread ...
http://forum.userstyles.org/discussion/35421/multi-column
*/

}
