style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tweakers.net") {
/* General */
/* General */
body {  background-color: #313232;	}   /* Dim the lamp */
h2 {	margin-bottom: 5px !important;	}
td {	padding: 4px !important;		}

#layout {
	width: 1270px;  /* Set width appropiate for a minimum of 1280x1024px*/
	margin-left: auto;
	margin-right: auto;
	background: none repeat scroll 0 0 #262828 !important;
	box-shadow: 0 0 5px #888 !important;
	-moz-box-shadow: 0 0 5px #888 !important;
	-o-box-shadow: 0 0 5px #888 !important;
	-webkit-box-shadow: 0 0 5px #888 !important;
}

/* #top |> */
#top {
	border-bottom: 1px solid #262828 !important;
}
	/* #top #menubar */
	#menubar {
		background: none repeat scroll 0 0 #262828 !important;
		border-radius: 6px 6px 1px 1px !important;
		border-color: #262828 !important;
		box-shadow: none !important;
		-moz-box-shadow: none !important;
		-o-box-shadow: none !important;
		-webkit-box-shadow: none !important;
		width: 100% !important;
		z-index: 1 !important;
		position: relative !important;
		bottom: -50px !important;
	}
	#menu {
		margin: 0 50px !important;
		width: auto !important;
	}
	#logo {
		left: -20px !important;
	}
	#logo + .sitename {
		left: 29px !important;
	}
	#menu .sitename + ul {
		margin-left: 231px !important;
	}
	#userbar {
		z-index: 100 !important;
	}
	
	/* #top #searchbar */
	#searchbar {
		background: none repeat scroll 0 0 transparent !important;
		height: 50px !important;
		position: relative;
		top: -50px;
		margin: 0 !important;
	}
	#search {
		height: 24px;
		padding-top: 0 !important;
		position: relative;
		top: 31px;
		width: 100% !important;
	}
	#search > a {
		background: linear-gradient(#F5F6F6, #EDEEEE) repeat scroll 0 0 #EDEEEE;
		background: -moz-linear-gradient(#F5F6F6, #EDEEEE) repeat scroll 0 0 #EDEEEE;
		background: -o-linear-gradient(#F5F6F6, #EDEEEE) repeat scroll 0 0 #EDEEEE;
		background: -webkit-linear-gradient(#F5F6F6, #EDEEEE) repeat scroll 0 0 #EDEEEE;
		box-shadow: inset 0 0 3px 3px #B41039;
		-moz-box-shadow: inset 0 0 3px 3px #B41039;
		-o-box-shadow: inset 0 0 3px 3px #B41039;
		-webkit-box-shadow: inset 0 0 3px 3px #B41039;
		border-radius: 2px;
	} 
	#search #true {
		margin: -7px 15px 0 2px !important;
		padding: 7px;
	}
	#search #streamone {
		margin: -7px 2px 0 2px !important;
		padding: 7px 7px 2px 7px;
	}
	
	/* #top #tweakbase && #top .galleryHeadingContainer */
	/* Tweak it so that it is more in line with the rest of the tabs on tweakers*/
	.galleryHeadingContainer, #tweakbase {
		background: #262828 !important;
		padding: 0 0 0 270px !important;
	}
	.galleryHeadingContainer > div, #tweakbase > div  {
		background: linear-gradient(#F5F6F6, #EDEEEE) repeat scroll 0 0 #EDEEEE;
		background: -moz-linear-gradient(#F5F6F6, #EDEEEE) repeat scroll 0 0 #EDEEEE;
		background: -o-linear-gradient(#F5F6F6, #EDEEEE) repeat scroll 0 0 #EDEEEE;
		background: -webkit-linear-gradient(#F5F6F6, #EDEEEE) repeat scroll 0 0 #EDEEEE;
		padding: 6px 0 6px 20px !important;
		border-top: 1px solid #EDEEEE !important;
		border-top-left-radius: 6px;
		border-top-right-radius: 6px;
		position: relative;
		top: 1px;
	}
	.galleryHeadingContainer .galleryHeading .karma {
		margin-right: 6px;
	}
	.pageTabsContainer, #top #tabs {
		background: #262828 !important;
		padding-left: 270px;	
	}
	.pageTabsContainer .pageTabs, #top #tabs .pageTabs {
		background: none repeat scroll 0 0 #EDEEEE;
		width: 1000px !important;
		border-bottom-left-radius: 6px;
		border-bottom-right-radius: 6px;
	}
	.pageTabsContainer .pageTabs ul, #top #tabs .pageTabs ul {
		background: none repeat scroll 0 0 #EDEEEE;
		border-bottom: 1px solid #C3C6C6;
		border-bottom-left-radius: 6px;
		border-bottom-right-radius: 6px;
		padding: 0 20px 0 20px !important;
		position: relative;
		top: 2px;
	}
/* <| #top */

/* #b_tb |> */
#b_tb.takeover {
    border-bottom: 1px solid #E8EAEA;
    border-radius: 6px 6px 0 0;
    left: 280px;
    margin: 0 !important;
    position: relative;
}
/* <| #b_tb */

/* #contentArea |> */
#contentArea{
	background-color: #fff;	 /* leave white for non-transparent icons */
	border-radius: 6px;		 /* Get some stylish corners */
	margin-right: 0 !important; /* Align content right */
	padding: 10px !important;
	position: inherit !important;
	width: 980px !important;
}
#contentArea h1 {
	/* Minimize wasted space under headers */
	margin-bottom: 10px !important;
}

.producttoggle {
	padding-left: 10px !important;;
}

	/* Articles */
	.article h3				{	margin-bottom: 3px !important;  }
	.reactie, .articleColumn{   line-height: 1.2 !important;	}
	
	/* #reacties */
	#reacties				{	margin-left: 10px;			}
	#reacties .modFilter		{   width: auto !important;		}
	#reacties .eenReactie	{   margin-top: 5px !important;	}
	
	#reacties .reactieHeader {
		padding: 7px 0 5px !important;
	}
	#reacties .reactieHeader a[href="/*[[uid]]*/"] {
		/* Distinct your own replies */
		background-color: #0A93CA;
		border: 1px solid #0A8ABD;
		border-radius: 2px;
		color: #fff !important;
		padding: 1px;
		position: relative;
		top: 3px;
	}
	
	/* Turn 'reageer' link into a button */
	#reacties .commentLink {
		background-color: #EBF3F5;
		border: 1px solid #D9DDDF;
		border-radius: 2px 2px 2px 2px;
		color: #616464 !important;
		display: block;
		font-size: 11px;
		padding: 0px 5px;
		width: 42px;
	}
	#reacties .commentLink:hover {
		background: #0CA3D4;
		border: 1px solid #0B94BF;
		color: #fff !important;
		text-decoration: none !important;
	}

	/* Tracker */
	.popup.trackerPopup.fixed {
		/* Restyle and reposition the tracker */
		background-color: #262828 !important;
		position: absolute !important;
		left: auto !important;
		width: 270px !important;
		margin-left: -280px;
		min-width: 270px;
		top: 84px !important;
		bottom: auto !important;
	}
	.trackerPopup .trackeritem ul {
		margin: 3px 0 0 !important;
	}
	.trackerPopup .trackeritem li {
		padding: 3px 0 3px 3px !important;
	}
	.trackerPopup .trackeritem li .caption {
		/* Make contrast between text and background smaller */
		color: #898886 !important;
	}
	.popup.trackerPopup.fixed #trackerFixateLink {
		/* Remove unnecessary "fixeer" link from menu */
		display: none !important;
	}
	.popup.trackerPopup #trackerFixateLink {
		/* Tricky css way to 'remove' text from the right */
		float: right;
		white-space: pre;
		width: 47px;
	}
/* <| #contentArea */

/* #bottom |> */
#bottom {
	background: none repeat scroll 0 0 #262828 !important;
	position: relative!important;
}
#bottom .hr{
	display: none;
}
/* <| #bottom */
   
/* Special blocks on multiple pages */
	/* #fpaItems |> */
		/* #fpaItems - General */
		#fpaItems {
			background: linear-gradient(#F5F6F6, #EDEEEE) repeat scroll 0 0 #EDEEEE;
			background: -moz-linear-gradient(#F5F6F6, #EDEEEE) repeat scroll 0 0 #EDEEEE;
			background: -o-linear-gradient(#F5F6F6, #EDEEEE) repeat scroll 0 0 #EDEEEE;
			background: -webkit-linear-gradient(#F5F6F6, #EDEEEE) repeat scroll 0 0 #EDEEEE;
			border: 1px solid #DDDEDE;
			border-radius: 1px;
			box-shadow: 0 0 0 1px rgba(255, 255, 255, 0.33) inset;
			-moz-box-shadow: 0 0 0 1px rgba(255, 255, 255, 0.33) inset;
			-o-box-shadow: 0 0 0 1px rgba(255, 255, 255, 0.33) inset;
			-webkit-box-shadow: 0 0 0 1px rgba(255, 255, 255, 0.33) inset;
			margin-bottom: 15px !important;
			width: 100%;
		}
		#fpaItems .fpaItem {
			height: auto !important;
		}   
		#fpaItems .fpaItem a {
			border-radius: 2px;
			display: block;
			overflow: hidden;
			text-decoration: none;
		}
		#fpaItems .fpaItem div.textContainer {
			background-color:#444444 !important;
		}
		#fpaItems .fpaItem:hover .imageContainer {
			opacity:0.85 !important;
		}
			
		/* #fpaItems - Frontpage */
		div#fp_tabs_container ~ #fpaItems {
			float: right;
			width: 334px;
		}
		div#fp_tabs_container ~ #fpaItems .fpaItem {
			padding: 2px !important;
			width: 163px !important;
		}
		div#fp_tabs_container ~ #fpaItems .fpaItem a {
			width: 163px;
		} 
		div#fp_tabs_container ~ #fpaItems .fpaItem img {
			width: 163px !important;
			height: 81px !important;
		}
		div#fp_tabs_container ~ #fpaItems .fpaItem div.textContainer {
			font-size: 13px !important;
			line-height: 15.5px !important;
			height: 60px !important;
			padding: 5px 9px !important;
		}
		
		/* #fpaItems - Reviews */
		h2 ~ #fpaItems {
			width: 100%;
		}
		h2 ~ #fpaItems .fpaItem {
			padding: 4px 2px !important;
			width: 118px !important;
		}
		h2 ~ #fpaItems .fpaItem:first-of-type {
			padding-left: 4px !important;
		}
		h2 ~ #fpaItems .fpaItem a {
			width: 118px;
		}
		h2 ~ #fpaItems .fpaItem img {
			width: 118px !important;
			height: 59px !important;
		}
		h2 ~ #fpaItems .fpaItem div.textContainer {
			font-size: 12px !important;
			line-height: 14px !important;
			padding: 9px 9px 18px 9px !important;
		}
		
		/* #fpaItems - Over Tweakers */
		.article  > p ~ #fpaItems .fpaItem, .article  > p ~ #fpaItems .fpaItem .textContainer {
			height: auto !important;
		}
		
		
	/* <| #fpaItems */
	
	/* Categorybrowser */
	#categoryBrowser {
		margin-bottom: 15px !important;
	}
/* Special blocks on multiple pages */

