style.css:
@namespace url(http://www.w3.org/1999/xhtml);







@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {











ol#posts li.post.new_post {



background: #ffffff url('http://24.media.tumblr.com/tumblr_mcngzby1tN1r7xrdmo1_r2_1280.png') bottom right no-repeat;



}







img[src*="http://24.media.tumblr.com/tumblr_mcngzby1tN1r7xrdmo1_r2_1280.png"]{



    width:0;



    height:70px;



    padding-right:250px;



    opacity: 0;



}



#posts .post .arrow {

height: 0px;

left: -7px;

position: absolute;

top: 25px;

width: 8px;

}





}
