style.css:
@-moz-document url-prefix("http://zeq2.com/lite/forums/") {
* {
background: rgba(255, 224, 0, 0.09)!important;
}
img.header {
margin: -100px 0 10px -7px;
padding-bottom: 20px;
border-bottom: 1px solid #DDD;
}
#content h3 {
border: 3px solid #5D5F60;
}
#content #location {
margin: 0 0;
padding: 4px;
background: #3B6EAD;
padding-right: 200px;
border: 0px solid #3B6EAD;
color: white;
}
#content h3 {
border: 0px solid #5D5F60;
}
#content h3 {
border: 0px solid #5D5F60;
}
#side h3, #side h3 a {
background: 0;
font: 0px Arial;
line-height: 1;
letter-spacing: -0.5px;
color: #454545;
}
div h2, div h3, div h4 {
font: normal small-caps bold 12px Verdana;
color: #C8C8C8;
height: 10px;
margin: 0;
margin-top: 0px;
padding: 4px;
background: #5D5F60;
}
body {
font: normal normal normal 12px comic sans-serif;
color: #8B8B8B;
}
}
