style.css:
@-moz-document url-prefix("https://apps.facebook.com/chefville/") {
/* remove ad contents */
div#rightCol,
div#external_links,
div#pagelet_bluebar
{
  display: none !important;
}

/* un-block game contents */
div#mainContainer div#contentCol>div#contentArea
{
 display: block !important;
}

/* fit-window chain */
html,
body,
body>div._li,
div#globalContainer,
div#content,
div#content>div,
div#mainContainer,
div#contentCol,
div#contentArea,
div#pagelet_canvas_content,
div#app_content_180751008671144,
div#pagelet_iframe_canvas_content,
div#pagelet_iframe_canvas_content>div,
iframe#iframe_canvas,
div#mainContainer div#contentCol>div#contentArea
{
 width:   100% !important;
 height:  100% !important;
 border:  none 0px !important;
 padding: 0px !important;
 margin:  0px !important;
 position: static !important;
}

}

@-moz-document url-prefix("https://fb.cooking.zynga.com/flash.php") {
/* remove ad contents */
div#zbarContainer,
body>div
{
  display: none !important;
}

/* rescue important contents  */ 
body>div#buttons,
body>div#appFrame,
body>div#zsc_console,
body>div#fb-root
{
 display: block !important;
}


/* fit-window chain */ 
html,
body,
div#appFrame,
div#flashFrame,
div#flashFrame>div,
div#flashOuterContainer,
object#flashapp
{
 width:  100% !important;
 height: 100% !important;
 margin:  0px !important;
 padding: 0px !important;
 border:  none 0px !important;
}

/* re-place mailButton */
div#buttons
{
 position: absolute;
 top: 0px;
 right: 380px !important;
 margin-left: 20px;
 width: auto;
 height: 50px;
 z-index: 100;
}
}
