.wrapper-list li.cat-top-right {
	background: #253545;
	border: 1px solid #3C6083;
	border-bottom: 0px;
	border-left: 0px;	
}
#content_actions {
	background: #303030;
    border: 1px solid #3C6083;
}
.wrapper-list li.cat-bottom-left {
	background: #253545;
    border: 1px solid #3C6083;
    border-right: 0px;
}
.wrapper-list li.cat-bottom-middleleft {
	background: #253545;
    border: 1px solid #3C6083;
    border-left: 0px;
	border-right: 0px;
}
.wrapper-list li.cat-bottom-middle {
	background: #253545;
    border: 1px solid #3C6083;
    border-left: 0px;
	border-right: 0px;
}
.wrapper-list li.cat-bottom-middleright {
	background: #253545;
    border: 1px solid #3C6083;
    border-left: 0px;
	border-right: 0px;
}
.wrapper-list li.cat-bottom-right {
	background: #253545;
    border: 1px solid #3C6083;
    border-left: 0px;
}
}

options.value:
/*Message Colors Settings: Fix Black on Black*/
.messageText span[style^='color: #000000'] {
	color: #c8c8c8 !important;
}

/*Avatar Settings: Grayscale*/
span.avatarContainer img,
div.avatarHolder img {
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
}

/*Avatar Settings: Darken*/
span.avatarContainer img,
div.avatarHolder img {
    -webkit-filter: brightness(50%);
    -moz-filter: brightness(50%);
}

/*Avatar Settings: Grayscale & Darken*/
span.avatarContainer img,
div.avatarHolder img {
    -webkit-filter: brightness(50%) grayscale(100%);
    -moz-filter: brightness(50%) grayscale(100%);
}

/*Avatar Settings: Hide*/
div.avatarHolder a img,
div.messageUserInfo span.arrow,
div.messageUserInfo span.before,
div.messageUserInfo span.after {
	display: none;
}
.messageUserBlock div.avatarHolder {
    background-color: #1D1D1D;
	padding: 0px;
    border-top: 0px;
}
.messageUserBlock div.avatarHolder .onlineMarker {
    position: absolute;
    top: 18px;
    left: 1px;
}
span[style^="font-weight: bold; position: absolute; top: 95px; right: 12px;"] {
	font-weight: normal !important;
	top: 22px !important;
	right: -14px !important;
}
div.quickReply.message div.messageUserInfo {
	display: none;
}
#QuickReply {
	margin-left: 0px;
}

/*User Text Setting: Hide Them*/
.messageUserBlock .userTitle {
    display: none;
}

/*Post Count Badge Setting: Hide*/
.messageUserBlock span.user-rank-pips {
	display: none;
}

/*Portal Featured Content Bar: Remove it*/
/*Portal Featured Content Bar*/
#featured_container {
    display: none;
}

/*Portal Featured Content Bar: Fix it*/
#featured_container {
    text-align: center;
}
.featured_item {
    max-width: 49.6%;
}
#ad_container {
	display: none !important;
}

/*Did you support GBATemp by buying from nds-card?: Yes*/
div[style="text-align: center;"] a[href*="http://www.nds-card.com"]{
	display: none;
}
ol.messageList div[class=""][style=""] {
	display: none;
}
}
@-moz-document url-prefix("https://gbatemp.net/forums/"), url-prefix("http://gbatemp.net/forums/") {
#content > .pageWidth > .pageContent > aside > .sidebar > .section:nth-of-type(3) {
	display: none;
}
}
