style.css:
@-moz-document url-prefix("http://share.dmhy.org/"), url-prefix("http://share.dmhy.me/") {

body   {
    background: url("/*[[link]]*/") !important; 
    background-position: /*[[x]]*/ /*[[y]]*/ !important;
    background-attachment: fixed !important;
    background-repeat: no-repeat !important;
    width:90% !important;
    margin:0 auto !important;
}

div#top  {
    background: url("http://i.minus.com/iKeL3KHxpjxtL.png")!important;
    background-position: 8% 0% !important;
    background-attachment: no-fixed !important;
    background-repeat: no-repeat !important;
    height:120px!important;
}

div#top a img  {
    opacity:0 !important;
    width: 500px !important;
    height: 120px !important;
}

div.header  {
    background-color:rgba(255, 255, 255, 0) !important;
}

div.links  {
    background-color:rgba(255,255,255,0.3) !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
    position: fixed !important;
    padding: 12px 5px 10px 5px !important;
    color: gray !important;
    right: 10px !important;
}
.header .links a  {
    color: gray !important;
}

div.top_sort  {
    background-color:rgba(255, 255, 255, 0) !important;
}

div.menu  {
    background-color:rgba(255, 255, 255, 0.3) !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
    padding: 5px  !important;
    margin-top:5px !important;
    margin-bottom:5px !important;
    margin-left:20px !important;
}

div.menu li {
    background-color:rgba(255, 255, 255, 0.8) !important;
    margin-left:3px !important;
}

li > a {
    background-color:rgba(255, 255, 255, 0.3) !important;
}

div#expand-button  {
    background: none !important;
    background-color:rgba(255, 255, 255, 0.3) !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
    padding: 5px  !important;
    margin-top:5px !important;
    margin-bottom:5px !important;
    margin-left:20px !important;
    width: 50px !important;
}

div#team-expand , div#team-collapse {
    opacity:0.8 !important;
    margin-left: 0px!important;
}

div#team-dialog > div {
    background: none !important;
}

div#team-dialog li.team-item {
    background-color:rgba(255, 255, 255, 0.7) !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
    padding: 5px  !important;
    margin-top:5px !important;
    margin-left:5px !important;
    border-style: none !important;
}

#team-dialog a {
    color: rgb(80,80,80) !important;
    font-weight: bold !important;
    margin:0 auto !important;
}

div.main  {
    background-color:rgba(255, 255, 255, 0) !important;
}

div#mini_jmd.table.fl  {
    background-color:rgba(255, 255, 255, 0.5) !important;
    border-style: none !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
    border-radius: 0px;
}

div#mini_jmd.table.fl div.nav_title  {
    background-color:rgba(255, 255, 255, 0.3) !important;
    color: rgba(0, 0, 0, 1) !important;
}

div#mini_jmd.table.fl div.nav_title a {
    background-color:rgba(255, 255, 255, 0.3) !important;
    color: rgba(0, 0, 0, 1) !important;
}

div#mini_jmd.table.fl tr.even {
    background-color:rgba(255, 255, 255, 0) !important;
}

div#mini_jmd.table.fl tr.odd {
    background-color:rgba(255, 255, 255, 0.3) !important;
}

div#mini_jmd.table.fl th {
    background-color:rgba(100, 100, 100, 0.3) !important;
    border-radius: 0px !important;
}

div#mini_jmd.table.fl a {
    border: 1px solid gray !important;
}


div.quick_search  {
    opacity:0.5 !important;
    background-color:rgba(255, 255, 255, 1) !important;
    border: none !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
}

div.clear  {
    background-color:rgba(255, 255, 255, 0) !important;
    border: none !important;
}

div.clear > div > a {
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
    width: 110px !important;
}

div.announce {
    background-color:rgba(255, 255, 255, 0.8) !important;
    border: none !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
    margin: 5px 10px 5px 120px !important;
}

.main > div.table.clear ,table#topic_list.tablesorter {
    background-color:rgba(0, 0, 0, 0) !important;
    box-shadow: none !important;
}


table.tablesorter tr th {
    color: gray !important;
    background-color:rgba(255, 255, 255, 0.8) !important;
    border-radius: 5px;
}

table.tablesorter tbody tr.even {
    color: black !important;
    height: 45px !important;
    background-color:rgba(255, 255, 255, 0.5) !important;
}

table.tablesorter tbody td {
    background-color:rgba(255, 255, 255, 0) !important;
}

table.tablesorter tbody tr.odd  {
    color: black !important;
    height: 45px !important;
    background-color:rgba(255, 255, 255, 0.8) !important;
}

.nav_title  {
    background-color:rgba(205, 205, 255, 0) !important;
    color: gray !important;
}

.nav_title a  {
    color: black !important;
    font-weight: bold !important;
} 

div.fl , div.fr {
    background: none !important;
    background-color:rgba(255, 255, 255, 0.5) !important;
    padding:10px 10px 10px 10px !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
    margin: 10px 0 5px 8px !important;
    border-style: none !important;
}

div.table  {
    color: gray !important;
    background-color:rgba(255, 255, 255, 0.5) !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
    border-style: none !important;
    border-radius: 10px;
    padding: 3px !important;
    margin-bottom:10px !important;
    border-radius: 0px!important;
}

div.topics_bk , div.avatar.box ,div.topics_cult.box {
    background-color:rgba(0, 0, 0, 0) !important;
    border-style: none !important;
}

div.user-sidebar {
    width: 17% !important;
    color: gray !important;
    background-color:rgba(255,255,255, 0.8) !important;
    padding: 3px !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
    margin-bottom:10px !important;
}


div.topic-main .box  {
    color: black !important;
    background-color:rgba(255,255,255, 0.7) !important;
    padding: 3px !important;
    box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
    margin-bottom:10px !important;
    border-style: none !important;
    border-radius: 0px;
}

div#play-asia  {
    display: none !important;
}

ul.ui-widget-header  {
    background-image: none !important;
    background-color:rgba(255,255,255,0.8) !important;
    border-style: none !important;
    border-radius: 0px;
}

div.file_list  {
    opacity:0.7 !important;
}

div#comment_add {
    background-color:rgba(255,255,255,0.8) !important;
    border-style: none !important;
    border-radius: 0px;
}

table.list_style tr.even , div#user-menu p , dl.zend_form dt , .even{
    color: black !important;
    background-color:rgba(255, 255, 255, 0.5) !important;
}


table.list_style tr.odd  , dl.zend_form dd , .odd{
    color: black !important;
    background-color:rgba(255, 255, 255, 0.8) !important;
}

table.list_style {
    background-color:rgba(255, 255, 255, 0) !important;
}

div#user-menu  {
    border: 1px solid #FFF !important;
    background-color:rgba(255, 255, 255, 0.3) !important;
}
#user-menu p a{
    color: black !important;
    font-weight: bold !important;
}

div#cms-5.main > div , div#cms-1.main > div , div#cms-2.main > div {
    background: rgba(255,255,255,0.5)!important;
    border-style: none !important;
}
}
/*[[ads]]*/
