style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(tumblr.com) {
.new_post_label .new_post_label_icon{
    background-image:none;
    background-size:513px 190px;
    background-repeat:no-repeat;
    position:absolute;
    display:block;
    width:76px;
    height:90px;
    top:5px;
    left:0px;
    opacity:0;
}
 
#posts .post.new_post .new_post_label {
    font-family:'Calibri', 'Trebuchet MS', sans-serif;
    font-size:20px;
    text-transform:lowercase;
    font-variant:small-caps;
    text-shadow:0.1em 0.1em rgba(0,0,0,1);
    margin-top:5px;
}

ol#posts li.post.new_post {
    background: #000000 url('http://i84.photobucket.com/albums/k30/barkfang/tdcbar.png') center center no-repeat;
}

ol#posts li.new_post{
background-color:#000000 !important;
}

ol#posts li.new_post .arrow{
background:url('http://i84.photobucket.com/albums/k30/barkfang/left-arrow-dashboard-icon-right.png') no-repeat scroll right center transparent!important;
}

a#new_post_label_text, a#new_post_label_photo, a#new_post_label_quote, a#new_post_label_link, a#new_post_label_chat, a#new_post_label_audio, a#new_post_label_video{
color:#efefef !important;
}
}
