style.css:
/* Skin JVC Rouge & Noir - Responsive - by Tiger  */

/* Vous pouvez modifier ce skin, mais pas diffuser la version modifiée sans mon accord */

/* CORPS PRINCIPAL */

@-moz-document url-prefix(http://www.jeuxvideo.com/forums/), url-prefix(https://www.jeuxvideo.com/forums/) {
.bloc-boutique-droite, .bloc-pres1, .heading-pres1 {
display: none !important;
}/* Supprime les blocs de pubs sur les forums */
}


@-moz-document regexp('http://www.jeuxvideo.com/(?!profil)([^"]*)'), regexp('https://www.jeuxvideo.com/(?!profil)([^"]*)') {
.layout--withBackground {
background-color: #2a2a2a !important;
background-image: url("http://i.imgur.com/WmoIHlv.jpg") !important;
}/* Modifie le fond du site */
}


@-moz-document url-prefix(http://www.jeuxvideo.com/forums/), url-prefix(https://www.jeuxvideo.com/forums/) {
#bloc-meta-titre-jeu {
background: none repeat scroll 0 0 rgba(0, 0, 0, 0.20)  !important;
}/* Supprime le fond du bloc de la fiche de jeu */

.navbar-game {
background: rgba(10, 10, 10, 0.25) none repeat scroll 0 0  !important;
}/* Modifie le fond du bas du bloc de la fiche de jeu */

.navbar-game .navbar-brand:active, .navbar-game .navbar-brand:hover, .navbar-header > span > span:hover {
color: #cc3430  !important;
}/* Modifie la couleur du titre sur les fiches de jeu */

.navbar-game .navbar-brand:active .caret, .navbar-game .navbar-brand:hover .caret {
border-top-color: #cc3430  !important;
}/* Modifie la couleur de la puce sur les fiches de jeu */
}


@-moz-document url-prefix(http://www.jeuxvideo.com/gta), url-prefix(https://www.jeuxvideo.com/gta) {
#content { 
padding-top: 20px !important; 
}/* Ajoute un espace entre la barre de menu et la page */
}


/*[[pub]]*/

@-moz-document domain(jeuxvideo.com) {
.header-top, .header-top .logo .icon-jeuxvideo, .account-number-mp, .account-number-notif, .nav-link-account, .nav-link-search, .nav-open .nav-open-hamburger, .nav-open .nav-open-text, .account-pseudo, .header-bottom .nav-lvl1-item > .nav-link, .nav-lvl2-item > .nav-link, .nav-lvl2-item-conf > .nav-link:hover, #header-search .search, #header-search .search-keyword-link, #header-search-close, .nav-lvl2-item > .nav-lv2-link-repeat > i {
color: #fff !important;
}

.nav-platform .nav-lvl1-item > .nav-link:hover, .account-number-mp:hover, .account-number-notif:hover, .nav-link-account:hover, .nav-link-search:hover, .nav-link-account:hover .account-pseudo, a#link-onglet_49916:hover, #header-search .search-keyword-link:hover, .global-menu:hover .nav-open .nav-open-text, .nav-open:hover .nav-open-hamburger {
color: #f2f2f2 !important;
}

.header-sticky {
border: 1px solid #303030 !important;
}

#jv-footer .footer-links {
display:none !important;
}

#content > div:nth-child(5) {
min-height: 20px !important;
height: 20px !important;
visibility: hidden !important;
}/* Supprime l'espace entre la barre de menu et la page */

/*[[spotify]]*/

body {
background-color: #2a2a2a  !important;
}

.jv-header-menu .jv-header-top .logo .nav-icon-jeuxvideo {
color: #fff  !important;
}/* Modifie le logo du site */

.jv-header-menu .jv-header-top .logo .nav-icon-com {
color: #CC3430  !important;
}/* Modifie le logo du site */

.jv-header-top {
background-color: #2a2a2a  !important;
}/* Modifie la couleur du haut du header */

.jv-header-sticky {
background-color: #383838  !important;
}/* Modifie la couleur de la barre des machines */ 
  
.jv-header-bottom, .jv-header-sticky {
background-color: #303030 !important;
}/* Modifie la couleur de la barre du menu */

.jv-nav-open, .jv-main-nav.on::after, .jv-nav-open.on .jv-nav-open-hamburger, .jv-nav-lvl1-item .nav-link:hover {
background-color: #333 !important;
}/* Compatibilité responsive pour le menu */

.jv-nav-primary, .jv-header-menu .jv-nav-platform {
background-color: #2a2a2a !important;
}/* Compatibilité responsive pour le menu */

.jv-nav-primary .jv-nav-lvl1-item:first-child {
border-top: 0.0625rem solid #2a2a2a  !important;
}/* Compatibilité responsive pour le menu */

.jv-header-menu .jv-nav-platform .jv-nav-lvl1-item, .jv-header-menu .jv-nav-primary .jv-nav-lvl1-item, .jv-header-menu .jv-nav-primary .jv-nav-lvl2-item {
border-bottom: 0.0625rem solid #2a2a2a  !important;
}/* Compatibilité responsive pour le menu */

.jv-header-bottom .jv-nav-primary .jv-nav-toggler:hover {
background-color: #333 !important;
}/* Compatibilité responsive pour le menu */

.jv-header-bottom .jv-nav-primary .jv-nav-toggler {
border-left: 1px solid #2a2a2a  !important;
}/* Compatibilité responsive pour le menu */

.jv-nav-lvl1-item-toggler:hover {
background: #333 !important;
}/* Compatibilité responsive pour le menu */

.jv-nav-lvl1-item-toggler .jv-nav-toggler-more, .jv-nav-lvl1-item-toggler .jv-nav-toggler-more::after, .jv-nav-lvl1-item-toggler .jv-nav-toggler-more::before {
background-color: #fff  !important;
}/* Compatibilité responsive pour le menu */

.jv-header-menu .jv-nav-lvl2, .jv-header-menu .jv-nav-primary .jv-nav-lvl1-item>.nav-link:hover, .jv-header-menu .jv-nav-primary .jv-nav-toggler-on, .jv-header-menu .jv-nav-primary .jv-nav-toggler-on ~ .nav-link {
background-color: #303030 !important;
}/* Modifie la couleur du menu */

.jv-header-menu .jv-nav-lvl2-item>.nav-link:hover {
background-color: #333 !important;
}/* Modifie la couleur du menu au passage de la souris */

.jv-nav-lvl2-item>.nav-lv2-link-repeat {
background-color: #2d2d2d !important;
}/* Modifie la couleur du menu */

.jv-nav-primary .jv-nav-toggler::after, .jv-nav-primary .jv-nav-toggler::before {
border-top-color: #fff !important;
}/* Modifie la couleur des puces du menu */

.jv-nav-lvl2-item > .nav-link {
border-bottom: 1px solid #2a2a2a  !important;
}/* Modifie la couleur des bordures du menu */

.jv-nav-lvl2-item>.nav-lv2-link-repeat>i {
color: #fff  !important;
}/* Modifie la couleur de la flèche */

.jv-nav-lvl2-item-conf > .nav-link {
color: #fff  !important;
}/* Modifie la couleur de configurer */

.jv-nav-primary .jv-nav-lvl1-item:last-child {
display: none !important;
}/* Supprime le Store */

.jv-nav-platform .jv-nav-lvl1-item > .nav-link {
color: #fff  !important;
}/* Modifie la couleur des machines sur la barre des machines */

#jv-header-search {
background: #2a2a2a  !important;
}/* Modifie la couleur du background quand on fait une recherche */

#jv-header-search .search input::-moz-placeholder {
color: #fff  !important;
}/* Modifie la couleur quand on fait une recherche */

#jv-header-search .search > .autocomplete-jv {
border: 1px solid #2d2d2d  !important;
}/* Modifie la couleur de la bordure du menu des suggestions dans la recherche */

#jv-header-search .search > .autocomplete-jv {
background: #2a2a2a none repeat scroll 0 0  !important;
}/* Modifie la couleur de fond du menu des suggestions dans la recherche */

#jv-header-search .search > .autocomplete-jv li.selected {
background: #333 !important;
}/* Modifie la couleur de fond du menu des suggestions dans la recherche au passage de la souris */

#jv-header-search .search > .autocomplete-jv li {
color: #FFFFFF  !important;
}/* Modifie la couleur des titres du menu des suggestions dans la recherche */

#jv-header-search .search input {
border-color: #cc3430  !important;
}/* Modifie la couleur de la bordure */  
  
#jv-header-search .search button {
background-color: #cc3430  !important;
}/* Modifie la couleur du bouton OK */ 

#jv-header-search .jv-search-keyword-label {
color: #cc3430  !important;
}/* Modifie la couleur des exemples de recherche */

.jv-nav-account-user.open::after, .jv-nav-account-notif.open::after, .jv-nav-account-mp.open::after {
border-bottom: 0.4375rem solid #2d2d2d !important; 
}/* Modifie la couleur de la puce */ 

.jv-nav-dropdown-container-top {
background: #2d2d2d !important;
border-bottom: 1px solid  #2a2a2a  !important;
}/* Modifie la couleur de fond du haut du menu du pseudo */ 

.jv-nav-dropdown-container {
background: #2d2d2d !important;
}/* Modifie la couleur de fond du menu du pseudo */
  
.account:nth-of-type(1) {
background: #2d2d2d !important;
}/* Modifie la couleur de fond du pseudo connecté du menu du pseudo */

.account {
background: #303030 !important;
border-top: 1px solid #2a2a2a  !important;
}/* Modifie la couleur de fond des alias du menu du pseudo */

.account-admin-link {
