border: 1px solid #353535  !important;
color: #fff  !important;
}/* Modifie la couleur des boutons du bas du menu au passage de la souris */

.jv-header-menu .jv-account-number-mp.has-notif::after, .jv-header-menu .jv-account-number-notif.has-notif::after {
