}/* Modifie la couleur du nombre de MPs et notifications */

.jv-nav-dropdown-title {
color: #fff  !important;
}/* Modifie la couleur du titre du menu MP */

.jv-nav-dropdown-link {
color: #fff  !important;
}/* Modifie la couleur du lien pour tout marquer comme lu */

.jv-nav-dropdown-link:hover {
color: #cc3430  !important;
}/* Modifie la couleur du lien pour tout marquer comme lu au passage de la souris */
  
.jv-nav-dropdown-unread {
