}/* Modifie la couleur du contenu du MP */

.jv-nav-dropdown-item-label:hover {
color: #cc3430  !important;
}/* Modifie la couleur du contenu du MP au passage de la souris */

.jv-nav-dropdown-item:hover {
background-color: #333 !important;
}/* Modifie la couleur des MPs et notifications au passage de la souris */

.wait::before {
border-left-color: #333 !important;
border-right-color: #333 !important;
border-bottom-color: #272727 !important;
border-top-color: #272727 !important;
}/* Modifie la couleur du spinner de chargement */

.wait::after {
border-bottom-color: #333 !important;
border-top-color: #333 !important;
border-left-color: #272727 !important;
border-right-color: #272727 !important;
}/* Modifie la couleur du spinner de chargement */

#jv-footer .external-links, #jv-footer .highlight {
