style.css:
/**
 * Stylish - Veoh
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.veoh.com/watch/") {

html {
    background-color: #000;
}

body {
    padding-top: 100px !important;
}

/* 右側の広告 */
#videoToolsContainer {
    position: absolute;
    top: -1000px;
    left: -1000px;
}

#videoPlayerContainer,
.veoh-video-player,
#videoPlayerContainer embed {
    height: 600px !important;
    width: 960px !important;
    background-color: #000;
}

/* 左側のコンテンツをページ全体にする */
#videoContainer {
    width: 100%;
}

/* 色々、非表示 */

#header,
#browseNav,
#videoDetails,
.video-title-container,
#relatedArrow,
#fdbk_tab,
#RelatedVideosContainer,
.watchExpandableBoxes,
#footer {
    display: none;
}
}
