options.value:
menupopup, .menupopup, popup, .popup {
	-moz-appearance: none!important;
	background-image: -moz-linear-gradient(rgba(0,0,0,.03),rgba(0,0,0,.3),rgba(0,0,0,.03)),
				-moz-linear-gradient(rgba(255,255,255,0) ,rgba(255,255,255,.5),rgba(255,255,255,0))!important;
	background-repeat: no-repeat!important;
	background-position: 27px !important;
	background-size: 1px 100%!important;
}

menuseparator, popup menuseparator,menulist menuseparator{
	-moz-appearance: none!important;
	/*-moz-margin-start:28px !important;*/
	border: none !important;
	background-image: -moz-linear-gradient(left, rgba(0,0,0,.03),rgba(0,0,0,.3),rgba(0,0,0,.03)),
				-moz-linear-gradient(left, rgba(255,255,255,0) ,rgba(255,255,255,.5),rgba(255,255,255,0))!important;
        padding: .5px !important;
	margin-top:3px !important;
	margin-bottom:2px !important;
}

menulist > popup,menulist > menupopup {background-image: none !important;}
menupopup.menu-iconic-text{margin:1px !important; -moz-margin-start: 13px !important;}
.menu-text{-moz-margin-start: 13px !important;}

menupopup, .menupopup, popup, .popup {
	-moz-appearance: none!important;
	background-image: -moz-linear-gradient(left, rgba(0, 0, 0, 0.06) 0%, rgba(217, 217, 217, 0.45) 50%, rgba(255, 255, 255, 0.64) 100%)!important;
	background-repeat: repeat-y!important;
	background-position: 26px !important;
	background-size: 2px 100%!important;
}

menuseparator, popup menuseparator,menupopup menuseparator{
	-moz-appearance:none !important;
	-moz-margin-start:26px !important;
	border-top: none !important;
	border-bottom: none !important;
	background-image: -moz-linear-gradient(top, rgba(0,0,0, 0.06) 0%, rgba(217, 217, 217, 0.45) 50%, rgba(255, 255, 255, 0.64) 100%)!important;
	margin-top: 2px !important;
	margin-bottom: 2px !important;
	height: 2px !important;
	min-height: 2px !important;
}

.menu-text {margin:1px !important; -moz-margin-start: 15px !important;} 
menupopup .menu-iconic-text{-moz-margin-start: 15px !important;}
menulist > popup, menulist > menupopup {background-image:none !important;}
