style.css:
@-moz-document url-prefix("http://www.bing.com/search") {
.sa_mc p, input.sw_qbox, .rrrs_itm, .sc_hl1, .sa_drw{
  font-family: tahoma;
}
.sa_drw{
  font-size:.8em;
}
.sb_tlst{
  margin-bottom:4px;
}
.sb_alert a:hover, .rrrs_itm a:hover, .sc_hl1 a:hover, .sb_vdl a:hover, .sb_tlst a, .sb_tlst a:hover, .sb_add a{
  text-decoration:none;
}
.sb_tlst a, .sb_add a{
  border-bottom:1px dashed #1096FF;
  padding-bottom:1px;
  color:#0061E0;
}
.sb_tlst a:hover{
  border-bottom:none;
  color:#007BFF;
}

.sb_alert a:hover, .rrrs_itm a:hover, .sc_hl1 a:hover, .sb_vdl a:hover{
  color:#1096FF;
}
.sa_cc:hover{
  background-color:#f4fdff;
  box-shadow:0 1px 0 #e2f9ff;
}
.sb_ans{
  display:none;
}
}
