style.css:
/*@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");*/

@-moz-document url("chrome://browser/content/browser.xul") {
  :root {
    --toolbarbutton-active-boxshadow: none !important;
  }
  
  #toolbar-menubar dropmarker,#status4evar-status-bar dropmarker {display: none !important;}
  #status4evar-status-bar toolbarbutton image {margin-inline-end: 0 !important;}

  .urlbar-progress-layer {display: none !important;}

  #forward-button {
    margin-left: 0 !important;
    margin-right: -1px !important;
  }
  #forward-button, .tab-close-button, .tabbrowser-tab, .tab-background, toolbarbutton {
    transition: none !important;
  }
  .tabbrowser-tab:not([pinned]) image  {
    -moz-margin-start: 3px;
    -moz-margin-end: 3px;
  }
  .tab-icon-image:not([src]) {
    display: -moz-box !important;
  }

  .tabbrowser-tab {
    -moz-box-flex: 0 !important;
    min-width: -moz-min-content !important;
    width: -moz-min-content !important;
  }
  .tabbrowser-tab label,.tabbrowser-tab .tab-close-button {display: none !important;}
  .tabbrowser-tab[pending] {opacity: 0.5 !important;}
}
