style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
 domain("itmedia.co.jp")
,domain("atmarkit.co.jp")
,domain("monoist.atmarkit.co.jp") {
  /* 背景色を単一色で塗りなおします */
  /* repaint background */
  body { background: #F5F5F5!important; }

  /* メニュー上部広告を非表示にします */
  /* hide ads on top menus */
  div#ITN { display: none; }

  /* 記事内の広告を非表示にします */
  /* hide ads in article */
  div#ITR { display: none; }

  /* 記事下部の広告を非表示にします */
  /* hide ads under article */
  div#ITSB { display: none; }

  /**情報マネジメント**/
  /* 上部の広告を非表示にします */
  /* hide top ads */
  div.ad { display: none; }

  /* 上部の広告を非表示にします */
  /* hide top ads */
  div#header_ad { display: none; }

  /* hide ads */
  div#crec { display: none; }
  div#frec { display: none; }
  div#rrec { display: none; }
}
