style.css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@namespace html "http://www.w3.org/1999/xhtml";

scrollbar[orient="vertical"],scrollbar[orient="vertical"] > slider
{min-width: 9px !important;
 max-width: 9px!important;}
scrollbar[orient="horizontal"],scrollbar[orient="horizontal"] > slider
{min-height: 9px !important;
 max-height: 9px !important;
}

/*from: userstyles.org/styles/68363/scrollbar-black-twisty-worm-gear */
scrollbar > slider, scrollbar > slider > thumb, scrollbar > scrollbarbutton
{ -moz-appearance: none !important; }
scrollbar[orient='vertical'] > scrollbarbutton { 
min-height: 10px !important; }
scrollbar[orient='horizontal'] > scrollbarbutton { 
min-width: 8px !important; }

scrollcorner{opacity: 0 !important;}
scrollbar scrollcorner:hover {background:transparent !important;}

/* ack-fix for erratic behavior in FFX.  using fixed background-attachment on scrollbars located on html pages appears to break the scrollbar (ie: scrollbar thumb background-image)* /
html|* > scrollbar > slider > thumb {
		background-attachment: scroll;
}
/* *{-moz-user-select: text} selects scrollbar >:O */

/*[[vcolour]]*/
/*[[hcolour]]*/
/* */

/*******yellow V *********** /
scrollbar[orient='vertical'] > slider > thumb{
	background-image: -moz-repeating-linear-gradient( 
		135deg
		,hsla(60, 100%,  100%, 1) 4px
		,hsla(60, 100%,  50%, 1) 12px
		,hsla(60, 100%,  0%, 1)  20px
		,hsla(60, 100%,  50%, 1) 24px
	) !important; 
}
