style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4server.info") {

#kanan, iframe, noscript, blink, h2, p, a[target*="_blank"], script[type*="text/javascript"], div[id*="chitikaAdBeacon"], font[color="ff00ff"] {
    display: none !important;
    }
    
#center {
    margin: 15% 20% !important;
    }
    
font[color="000080"] {
    font-size: 20px !important;
    }
    
html, html > *, * {
	background-color: #CFCFCF !important;
	color: black !important;
	}
	
a, font[color="000080"] {
	color: black !important;
	text-shadow: 0 0 5px white !important;
	font-weight: bold !important;
	}

a:visited, font[color="000080"]:visited {
	color: #996633 !important;
    text-decoration: line-through !important;
	}

a:hover, a:focus, font[color="000080"]:hover {
	color: white !important;
	text-shadow: 0 0 4px black !important;
	transition: all 0.3s ease-in-out 0s !important;
	font-size: 25px !important;
    text-decoration: blink !important;
	}


}
