style.css:
@-moz-document domain('ggyy8.cc') {
	/* hide header and footer */
	#read_hd, 
	#ft {
		display: none !important;
	}

	/* hide search bar */
	.read_address_c > dd {
		display: none !important;
	}

	/* hide ad */
	.pd_tb_8_5 {
		display: none !important;
	}

	/* resize image container to maximize width */
	.read_middle, 
	.read_middle_img {
		width: 99% !important;
	}

	/* resize image to maximize width */
	.read_middle_img > img {
		width: 100% !important;
		height: auto !important;
	}
}
