style.css:
@-moz-document domain("trello.com") {
	.app-icon.logo {
	background-image: url(http://rastasoft.ir/cdn/license/80x17.light.png);
	background-position: center;
	}

	#board-header .board-name .text {
	  font-family: "b nazanin";
	}

	.list-guide .list-link strong,
	textarea,
	input,
	.list-card-title,
	.list-title,
	.card-label,
	.action-comment,
	.window-title-text,
	.window-title strong,
	.card-detail-desc,
	.checklist,
	.checklist .check_item_text {
		font: 12px/150% Tahoma;
		direction:rtl;
		text-align: right;
	}
	.list-title h2 {
		font: 16px/150% "B Nazanin", Tahoma;
		font-weight: bold;
	}
	.check_item_text  {
		direction:rtl;
		text-align:left;
	}
	.window-header {
		text-align:right;
	}

}
