style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
  domain("courses.fit.hcmus.edu.vn")
{
    .headingblock.header,.sideblock .header,.sideblock .header h2,.navbar,h2.main,h1,th.header
    {
        background: -moz-linear-gradient(top,  #7d7e7d 0%, #0e0e0e 100%) !important;
        background-color: transparent !important;
        border-color:white !important;
    }
    .content, .generalbox ,.coursebox,.coursebox .info,#header-home, #header,#course-view .section.main .side,.generaltable, .generaltable td,.forumheaderlist .discussion .starter,.userinfobox .side 
    {
        background-color: #3B3937 !important;
    }
    .posting
    {
        color:yellow !important;
    }
    .forumpost .starter,.forumpost .left
    {
        background-color: #3B3937 !important;
        border-color: white !important;
    }
    #layout-table, #right-column, #left-column,#page,.cal_popup_bg,.cal_popup_fg,HTML
    {
        background-color: #252423 !important;
    }
    #content > DIV> UL > LI > A > SPAN
    {
        color:black !important;
    }
    span
    {
        color:yellow !important;
    }
    div
    {
        color: #9ABBE3 !important;
    }
    a
    {
        color: #a5eeff !important;
    }
}
